"""
Simple Set Implementation

This module provides a simplified implementation of Python's set using hash tables.
It demonstrates the core concepts behind Python's set implementation including
hash table with open addressing, set operations, and memory efficiency.
"""

from typing import TypeVar, Generic, Iterator, Set as PySet, Optional
from src.chapter_01.hash_table import HashTable

T = TypeVar('T')

class SimpleSet(Generic[T]):
    """
    A simplified implementation of Python's set using hash tables.

    This demonstrates the core concepts behind Python's set implementation:
    - Hash table with open addressing (similar to dict)
    - Only keys, no values
    - Set operations (union, intersection, difference)
    """

    def __init__(self, iterable: Optional[PySet[T]] = None) -> None:
        self._hash_table = HashTable[T, bool]()
        if iterable:
            for item in iterable:
                self.add(item)

    def __len__(self) -> int:
        return len(self._hash_table)

    def __contains__(self, item: T) -> bool:
        return item in self._hash_table

    def add(self, item: T) -> None:
        """Add an item to the set."""
        self._hash_table[item] = True

    def remove(self, item: T) -> None:
        """Remove an item from the set."""
        del self._hash_table[item]

    def discard(self, item: T) -> None:
        """Remove an item from the set if present."""
        try:
            del self._hash_table[item]
        except KeyError:
            pass

    def __iter__(self) -> Iterator[T]:
        return iter(self._hash_table)

    def __repr__(self) -> str:
        items = [repr(item) for item in self]
        return f"SimpleSet({{{', '.join(items)}}})"

    def union(self, other: 'SimpleSet[T]') -> 'SimpleSet[T]':
        """Return the union of two sets."""
        result = SimpleSet(self)
        for item in other:
            result.add(item)
        return result

    def intersection(self, other: 'SimpleSet[T]') -> 'SimpleSet[T]':
        """Return the intersection of two sets."""
        result = SimpleSet()
        for item in self:
            if item in other:
                result.add(item)
        return result

    def difference(self, other: 'SimpleSet[T]') -> 'SimpleSet[T]':
        """Return the difference of two sets."""
        result = SimpleSet()
        for item in self:
            if item not in other:
                result.add(item)
        return result

"""
Real-World Applications of Dynamic Arrays

This module contains practical applications that demonstrate how dynamic arrays
are used in real-world scenarios like text editors and databases.
"""

from typing import Optional, List
from .dynamic_array import ProductionDynamicArray


class TextBuffer:
    """
    A simple text editor buffer using dynamic arrays.

    This demonstrates how dynamic arrays are used in real applications
    like text editors, where efficient insertion and deletion are crucial.
    """

    def __init__(self):
        self._lines = ProductionDynamicArray[str]()
        self._cursor_line = 0
        self._cursor_col = 0

    def insert_line(self, line_num: int, text: str) -> None:
        """Insert a new line at the specified position."""
        if not 0 <= line_num <= len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")

        self._lines.insert(line_num, text)
        if line_num <= self._cursor_line:
            self._cursor_line += 1

    def delete_line(self, line_num: int) -> str:
        """Delete and return the line at the specified position."""
        if not 0 <= line_num < len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")

        if line_num == self._cursor_line:
            self._cursor_line = max(0, self._cursor_line - 1)
        elif line_num < self._cursor_line:
            self._cursor_line -= 1

        return self._lines.pop(line_num)

    def get_line(self, line_num: int) -> str:
        """Get the line at the specified position."""
        if not 0 <= line_num < len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")
        return self._lines[line_num]

    def set_line(self, line_num: int, text: str) -> None:
        """Set the content of a line at the specified position."""
        if not 0 <= line_num < len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")
        self._lines[line_num] = text

    def get_all_lines(self) -> List[str]:
        """Get all lines as a list."""
        return list(self._lines)

    def line_count(self) -> int:
        """Get the number of lines."""
        return len(self._lines)

    def append_line(self, text: str) -> None:
        """Add a new line at the end."""
        self._lines.append(text)

    def insert_text(self, line_num: int, col: int, text: str) -> None:
        """Insert text at a specific position in a line."""
        if not 0 <= line_num < len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")

        current_line = self._lines[line_num]
        if not 0 <= col <= len(current_line):
            raise IndexError(f"Column {col} out of range for line {line_num}")

        new_line = current_line[:col] + text + current_line[col:]
        self._lines[line_num] = new_line

    def delete_text(self, line_num: int, start_col: int, end_col: int) -> str:
        """Delete text from a line and return the deleted text."""
        if not 0 <= line_num < len(self._lines):
            raise IndexError(f"Line number {line_num} out of range")

        current_line = self._lines[line_num]
        if not 0 <= start_col <= end_col <= len(current_line):
            raise IndexError(f"Invalid column range: {start_col}-{end_col}")

        deleted_text = current_line[start_col:end_col]
        new_line = current_line[:start_col] + current_line[end_col:]
        self._lines[line_num] = new_line

        return deleted_text

    def set_cursor(self, line: int, col: int) -> None:
        """Set the cursor position."""
        # Allow setting cursor at line 0, column 0 even when there are no lines
        if line == 0 and col == 0 and len(self._lines) == 0:
            self._cursor_line = line
            self._cursor_col = col
            return

        if not 0 <= line < len(self._lines):
            raise IndexError(f"Line number {line} out of range")

        # Allow setting cursor at column 0 even for empty lines
        if line < len(self._lines) and col > len(self._lines[line]):
            raise IndexError(f"Column {col} out of range for line {line}")

        self._cursor_line = line
        self._cursor_col = col

    def get_cursor_position(self) -> tuple[int, int]:
        """Get the current cursor position."""
        return (self._cursor_line, self._cursor_col)

    def __repr__(self) -> str:
        return f"TextBuffer({self.line_count()} lines, cursor at {self.get_cursor_position()})"


class DatabaseRecord:
    """A simple database record."""

    def __init__(self, id: int, name: str, value: float):
        self.id = id
        self.name = name
        self.value = value

    def __repr__(self) -> str:
        return f"Record(id={self.id}, name='{self.name}', value={self.value})"

    def __eq__(self, other: object) -> bool:
        if not isinstance(other, DatabaseRecord):
            return False
        return (self.id == other.id and 
                self.name == other.name and 
                self.value == other.value)

    def __hash__(self) -> int:
        return hash((self.id, self.name, self.value))


class SimpleDatabase:
    """
    A simple in-memory database using dynamic arrays.

    This demonstrates how dynamic arrays can be used for record storage
    in database systems.
    """

    def __init__(self):
        self._records = ProductionDynamicArray[DatabaseRecord]()
        self._next_id = 1

    def insert(self, name: str, value: float) -> int:
        """Insert a new record and return its ID."""
        record = DatabaseRecord(self._next_id, name, value)
        self._records.append(record)
        self._next_id += 1
        return record.id

    def get_by_id(self, id: int) -> Optional[DatabaseRecord]:
        """Get a record by ID."""
        for record in self._records:
            if record.id == id:
                return record
        return None

    def get_by_name(self, name: str) -> List[DatabaseRecord]:
        """Get all records with the given name."""
        result = []
        for record in self._records:
            if record.name == name:
                result.append(record)
        return result

    def get_by_value_range(self, min_value: float, max_value: float) -> List[DatabaseRecord]:
        """Get all records with values in the specified range."""
        result = []
        for record in self._records:
            if min_value <= record.value <= max_value:
                result.append(record)
        return result

    def delete_by_id(self, id: int) -> bool:
        """Delete a record by ID."""
        for i, record in enumerate(self._records):
            if record.id == id:
                self._records.pop(i)
                return True
        return False

    def update_by_id(self, id: int, name: str, value: float) -> bool:
        """Update a record by ID."""
        for record in self._records:
            if record.id == id:
                record.name = name
                record.value = value
                return True
        return False

    def get_all_records(self) -> List[DatabaseRecord]:
        """Get all records."""
        return list(self._records)

    def record_count(self) -> int:
        """Get the number of records."""
        return len(self._records)

    def clear(self) -> None:
        """Clear all records."""
        self._records.clear()
        self._next_id = 1

    def get_stats(self) -> dict:
        """Get database statistics."""
        if self.record_count() == 0:
            return {
                'record_count': 0,
                'avg_value': 0.0,
                'min_value': 0.0,
                'max_value': 0.0
            }

        values = [record.value for record in self._records]
        return {
            'record_count': self.record_count(),
            'avg_value': sum(values) / len(values),
            'min_value': min(values),
            'max_value': max(values)
        }

    def __repr__(self) -> str:
        return f"SimpleDatabase({self.record_count()} records)"


class CircularBuffer:
    """
    A circular buffer implementation using dynamic arrays.

    A circular buffer is a fixed-size buffer that overwrites the oldest
    data when full. This is useful for streaming data, audio processing,
    and other applications where you need a sliding window of recent data.
    """

    def __init__(self, capacity: int):
        """Initialize a circular buffer with the specified capacity."""
        if capacity <= 0:
            raise ValueError("Capacity must be positive")

        self._capacity = capacity
        self._buffer = ProductionDynamicArray[Optional[object]]()
        self._head = 0  # Index of the oldest element
        self._tail = 0  # Index of the next position to write
        self._size = 0  # Number of elements currently in the buffer

        # Initialize buffer with None values
        for _ in range(capacity):
            self._buffer.append(None)

    def put(self, item: object) -> None:
        """Add an item to the buffer, overwriting the oldest if full."""
        self._buffer[self._tail] = item

        if self._size < self._capacity:
            self._size += 1
        else:
            # Buffer is full, move head to next position
            self._head = (self._head + 1) % self._capacity

        # Move tail to next position
        self._tail = (self._tail + 1) % self._capacity

    def get(self) -> Optional[object]:
        """Get and remove the oldest item from the buffer."""
        if self._size == 0:
            return None

        item = self._buffer[self._head]
        self._head = (self._head + 1) % self._capacity
        self._size -= 1

        return item

    def peek(self) -> Optional[object]:
        """Get the oldest item without removing it."""
        if self._size == 0:
            return None
        return self._buffer[self._head]

    def is_empty(self) -> bool:
        """Check if the buffer is empty."""
        return self._size == 0

    def is_full(self) -> bool:
        """Check if the buffer is full."""
        return self._size == self._capacity

    def size(self) -> int:
        """Get the number of elements currently in the buffer."""
        return self._size

    def capacity(self) -> int:
        """Get the capacity of the buffer."""
        return self._capacity

    def clear(self) -> None:
        """Clear all elements from the buffer."""
        self._head = 0
        self._tail = 0
        self._size = 0

    def to_list(self) -> List[object]:
        """Convert the buffer to a list in order (oldest first)."""
        result = []
        for i in range(self._size):
            index = (self._head + i) % self._capacity
            result.append(self._buffer[index])
        return result

    def __repr__(self) -> str:
        return f"CircularBuffer(size={self._size}, capacity={self._capacity})"

"""
Dynamic Array Implementation

This module contains implementations of dynamic arrays with different growth strategies,
from basic to production-quality versions.
"""

import sys
import timeit
from typing import TypeVar, Generic, Optional, Iterator, List, Any
from enum import Enum
import math

T = TypeVar('T')

class GrowthStrategy(Enum):
    """Different growth strategies for dynamic arrays."""
    DOUBLING = "doubling"
    FIXED = "fixed"
    GOLDEN_RATIO = "golden_ratio"
    ADAPTIVE = "adaptive"


class DynamicArray(Generic[T]):
    """
    A basic dynamic array implementation with configurable growth strategies.

    This demonstrates the core concepts behind dynamic arrays:
    - Dynamic resizing with different growth strategies
    - Amortized O(1) append operations
    - Memory layout and object references
    - Trade-offs between memory usage and performance
    """

    def __init__(self, initial_capacity: int = 8, growth_factor: float = 2.0) -> None:
        """
        Initialize a dynamic array.

        Args:
            initial_capacity: Starting capacity of the array
            growth_factor: Factor by which to grow when resizing
        """
        if initial_capacity <= 0:
            raise ValueError("Initial capacity must be positive")
        if growth_factor <= 1.0:
            raise ValueError("Growth factor must be greater than 1.0")

        self._capacity = initial_capacity
        self._size = 0
        self._growth_factor = growth_factor
        self._array: List[Optional[T]] = [None] * initial_capacity

    def __len__(self) -> int:
        """Return the number of elements in the array."""
        return self._size

    def __getitem__(self, index: int) -> T:
        """Get element at index."""
        if not 0 <= index < self._size:
            raise IndexError("Index out of range")
        return self._array[index]

    def __setitem__(self, index: int, value: T) -> None:
        """Set element at index."""
        if not 0 <= index < self._size:
            raise IndexError("Index out of range")
        self._array[index] = value

    def append(self, value: T) -> None:
        """Add an element to the end of the array."""
        if self._size == self._capacity:
            self._resize(int(self._capacity * self._growth_factor))
        self._array[self._size] = value
        self._size += 1

    def insert(self, index: int, value: T) -> None:
        """Insert an element at the specified index."""
        if not 0 <= index <= self._size:
            raise IndexError("Index out of range")

        if self._size == self._capacity:
            self._resize(int(self._capacity * self._growth_factor))

        # Shift elements to make room
        for i in range(self._size, index, -1):
            self._array[i] = self._array[i - 1]

        self._array[index] = value
        self._size += 1

    def pop(self, index: Optional[int] = None) -> T:
        """Remove and return element at index (default: last element)."""
        if self._size == 0:
            raise IndexError("Cannot pop from empty array")

        if index is None:
            index = self._size - 1

        if not 0 <= index < self._size:
            raise IndexError("Index out of range")

        value = self._array[index]

        # Shift elements to fill gap
        for i in range(index, self._size - 1):
            self._array[i] = self._array[i + 1]

        self._size -= 1
        return value

    def remove(self, value: T) -> None:
        """Remove first occurrence of value."""
        for i in range(self._size):
            if self._array[i] == value:
                self.pop(i)
                return
        raise ValueError("Value not found")

    def _resize(self, new_capacity: int) -> None:
        """Resize the internal array to new capacity."""
        new_array: List[Optional[T]] = [None] * new_capacity
        for i in range(self._size):
            new_array[i] = self._array[i]
        self._array = new_array
        self._capacity = new_capacity

    def __iter__(self) -> Iterator[T]:
        """Iterate over elements in the array."""
        for i in range(self._size):
            yield self._array[i]

    def __contains__(self, value: T) -> bool:
        """Check if value is in the array."""
        for item in self:
            if item == value:
                return True
        return False

    def __repr__(self) -> str:
        """String representation of the array."""
        return f"DynamicArray({list(self)})"

    @property
    def capacity(self) -> int:
        """Get the current capacity of the array."""
        return self._capacity

    @property
    def load_factor(self) -> float:
        """Get the current load factor (size/capacity)."""
        return self._size / self._capacity if self._capacity > 0 else 0.0


class AdvancedDynamicArray(Generic[T]):
    """
    Advanced dynamic array with multiple growth strategies and optimizations.

    Features:
    - Multiple growth strategies
    - Memory usage tracking
    - Performance monitoring
    - Shrink on demand
    """

    def __init__(self, 
                 initial_capacity: int = 8, 
                 strategy: GrowthStrategy = GrowthStrategy.DOUBLING,
                 shrink_threshold: float = 0.25) -> None:
        """
        Initialize an advanced dynamic array.

        Args:
            initial_capacity: Starting capacity
            strategy: Growth strategy to use
            shrink_threshold: Load factor below which to shrink
        """
        if initial_capacity <= 0:
            raise ValueError("Initial capacity must be positive")
        if not 0.0 < shrink_threshold < 1.0:
            raise ValueError("Shrink threshold must be between 0 and 1")

        self._capacity = initial_capacity
        self._size = 0
        self._strategy = strategy
        self._shrink_threshold = shrink_threshold
        self._array: List[Optional[T]] = [None] * initial_capacity
        self._resize_count = 0
        self._total_elements_added = 0

    def _get_new_capacity(self, current_capacity: int) -> int:
        """Calculate new capacity based on growth strategy."""
        if self._strategy == GrowthStrategy.DOUBLING:
            return current_capacity * 2
        elif self._strategy == GrowthStrategy.FIXED:
            return current_capacity + 10
        elif self._strategy == GrowthStrategy.GOLDEN_RATIO:
            return int(current_capacity * 1.618)
        elif self._strategy == GrowthStrategy.ADAPTIVE:
            # Adaptive: use doubling for small arrays, golden ratio for large
            if current_capacity < 1000:
                return current_capacity * 2
            else:
                return int(current_capacity * 1.618)
        else:
            raise ValueError(f"Unknown growth strategy: {self._strategy}")

    def append(self, value: T) -> None:
        """Add an element to the end of the array."""
        if self._size == self._capacity:
            self._resize(self._get_new_capacity(self._capacity))

        self._array[self._size] = value
        self._size += 1
        self._total_elements_added += 1

    def _resize(self, new_capacity: int) -> None:
        """Resize the internal array to new capacity."""
