"""
BST Analyzer implementation for Chapter 6.

This module provides tools to analyze the performance and memory
characteristics of BST implementations.
"""

import sys
import timeit
from typing import TypeVar, Generic, Optional, Iterator, List, Dict, Any
from dataclasses import dataclass
from collections import deque

from .bst_node import BSTNode

T = TypeVar('T')

@dataclass
class TreeInfo:
    """Information about a binary search tree."""
    height: int
    size: int
    is_balanced: bool
    memory_usage: int
    average_depth: float
    leaf_count: int
    internal_node_count: int
    min_value: Optional[Any]
    max_value: Optional[Any]

class BSTAnalyzer:
    """
    Analyzer for Binary Search Tree implementations.

    This class provides tools to analyze the performance and memory
    characteristics of BST implementations.
    """

    @staticmethod
    def analyze_tree(root: Optional[BSTNode[T]]) -> TreeInfo:
        """Analyze a binary search tree."""
        if root is None:
            return TreeInfo(0, 0, True, 0, 0.0, 0, 0, None, None)

        height = BSTAnalyzer._calculate_height(root)
        size = BSTAnalyzer._calculate_size(root)
        is_balanced = BSTAnalyzer._is_balanced(root)
        memory_usage = BSTAnalyzer._calculate_memory_usage(root)
        avg_depth = BSTAnalyzer._calculate_average_depth(root)
        leaf_count = BSTAnalyzer._calculate_leaf_count(root)
        internal_node_count = size - leaf_count
        min_value = BSTAnalyzer._find_minimum_value(root)
        max_value = BSTAnalyzer._find_maximum_value(root)

        return TreeInfo(
            height=height,
            size=size,
            is_balanced=is_balanced,
            memory_usage=memory_usage,
            average_depth=avg_depth,
            leaf_count=leaf_count,
            internal_node_count=internal_node_count,
            min_value=min_value,
            max_value=max_value
        )

    @staticmethod
    def _calculate_height(node: Optional[BSTNode[T]]) -> int:
        """Calculate the height of a subtree."""
        if node is None:
            return -1
        return 1 + max(
            BSTAnalyzer._calculate_height(node.left),
            BSTAnalyzer._calculate_height(node.right)
        )

    @staticmethod
    def _calculate_size(node: Optional[BSTNode[T]]) -> int:
        """Calculate the size of a subtree."""
        if node is None:
            return 0
        return 1 + BSTAnalyzer._calculate_size(node.left) + BSTAnalyzer._calculate_size(node.right)

    @staticmethod
    def _is_balanced(node: Optional[BSTNode[T]]) -> bool:
        """Check if a tree is balanced (height difference <= 1 for all nodes)."""
        def check_balance(node: Optional[BSTNode[T]]) -> tuple[bool, int]:
            if node is None:
                return True, -1

            left_balanced, left_height = check_balance(node.left)
            right_balanced, right_height = check_balance(node.right)

            if not left_balanced or not right_balanced:
                return False, 0

            if abs(left_height - right_height) > 1:
                return False, 0

            return True, 1 + max(left_height, right_height)

        return check_balance(node)[0]

    @staticmethod
    def _calculate_memory_usage(node: Optional[BSTNode[T]]) -> int:
        """Calculate memory usage of a subtree."""
        if node is None:
            return 0

        node_size = sys.getsizeof(node) + sys.getsizeof(node.value)
        return node_size + BSTAnalyzer._calculate_memory_usage(node.left) + BSTAnalyzer._calculate_memory_usage(node.right)

    @staticmethod
    def _calculate_average_depth(node: Optional[BSTNode[T]]) -> float:
        """Calculate the average depth of nodes in a tree."""
        if node is None:
            return 0.0

        total_depth = 0
        node_count = 0

        def traverse_with_depth(node: Optional[BSTNode[T]], depth: int) -> None:
            nonlocal total_depth, node_count
            if node is None:
                return

            total_depth += depth
            node_count += 1

            traverse_with_depth(node.left, depth + 1)
            traverse_with_depth(node.right, depth + 1)

        traverse_with_depth(node, 0)
        return total_depth / node_count if node_count > 0 else 0.0

    @staticmethod
    def _calculate_leaf_count(node: Optional[BSTNode[T]]) -> int:
        """Calculate the number of leaf nodes in a tree."""
        if node is None:
            return 0

        if node.is_leaf():
            return 1

        return BSTAnalyzer._calculate_leaf_count(node.left) + BSTAnalyzer._calculate_leaf_count(node.right)

    @staticmethod
    def _find_minimum_value(node: Optional[BSTNode[T]]) -> Optional[T]:
        """Find the minimum value in a tree."""
        if node is None:
            return None

        current = node
        while current.left:
            current = current.left
        return current.value

    @staticmethod
    def _find_maximum_value(node: Optional[BSTNode[T]]) -> Optional[T]:
        """Find the maximum value in a tree."""
        if node is None:
            return None

        current = node
        while current.right:
            current = current.right
        return current.value

    @staticmethod
    def benchmark_operations(bst_class, operations: List[str], data_sizes: List[int]) -> Dict[str, Dict[int, float]]:
        """Benchmark common operations on BST implementations."""
        results = {}

        for operation in operations:
            results[operation] = {}

            for size in data_sizes:
                if operation == "insert":
                    setup = f"from src.chapter_06 import {bst_class.__name__}; bst = {bst_class.__name__}()"
                    stmt = f"[bst.insert(i) for i in range({size})]"
                elif operation == "search":
                    setup = f"from src.chapter_06 import {bst_class.__name__}; bst = {bst_class.__name__}(); [bst.insert(i) for i in range({size})]"
                    stmt = f"[bst.search(i) for i in range({size})]"
                elif operation == "delete":
                    setup = f"from src.chapter_06 import {bst_class.__name__}; bst = {bst_class.__name__}(); [bst.insert(i) for i in range({size})]"
                    stmt = f"[bst.delete(i) for i in range({size})]"
                elif operation == "traversal":
                    setup = f"from src.chapter_06 import {bst_class.__name__}; bst = {bst_class.__name__}(); [bst.insert(i) for i in range({size})]"
                    stmt = "list(bst.inorder_traversal())"
                elif operation == "range_search":
                    setup = f"from src.chapter_06 import {bst_class.__name__}; bst = {bst_class.__name__}(); [bst.insert(i) for i in range({size})]"
                    stmt = f"bst.range_search({size//4}, {3*size//4})"
                else:
                    continue

                time = timeit.timeit(stmt, setup=setup, number=1)
                results[operation][size] = time

        return results

    @staticmethod
    def compare_implementations(recursive_bst_class, iterative_bst_class, data_sizes: List[int]) -> Dict[str, Dict[str, float]]:
        """Compare performance between recursive and iterative BST implementations."""
        operations = ["insert", "search", "delete", "traversal", "range_search"]
        results = {}

        for operation in operations:
            results[operation] = {}

            for size in data_sizes:
                # Benchmark recursive implementation
                recursive_setup = f"from src.chapter_06 import {recursive_bst_class.__name__}; bst = {recursive_bst_class.__name__}()"
                if operation in ["search", "delete", "traversal", "range_search"]:
                    recursive_setup += f"; [bst.insert(i) for i in range({size})]"

                recursive_stmt = BSTAnalyzer._get_operation_stmt(operation, size)
                recursive_time = timeit.timeit(recursive_stmt, setup=recursive_setup, number=1)

                # Benchmark iterative implementation
                iterative_setup = f"from src.chapter_06 import {iterative_bst_class.__name__}; bst = {iterative_bst_class.__name__}()"
                if operation in ["search", "delete", "traversal", "range_search"]:
                    iterative_setup += f"; [bst.insert(i) for i in range({size})]"

                iterative_stmt = BSTAnalyzer._get_operation_stmt(operation, size)
                iterative_time = timeit.timeit(iterative_stmt, setup=iterative_setup, number=1)

                results[operation][size] = {
                    "recursive": recursive_time,
                    "iterative": iterative_time,
                    "ratio": recursive_time / iterative_time if iterative_time > 0 else float('inf')
                }

        return results

    @staticmethod
    def _get_operation_stmt(operation: str, size: int) -> str:
        """Get the statement for a given operation."""
        if operation == "insert":
            return f"[bst.insert(i) for i in range({size})]"
        elif operation == "search":
            return f"[bst.search(i) for i in range({size})]"
        elif operation == "delete":
            return f"[bst.delete(i) for i in range({size})]"
        elif operation == "traversal":
            return "list(bst.inorder_traversal())"
        elif operation == "range_search":
            return f"bst.range_search({size//4}, {3*size//4})"
        else:
            return ""

    @staticmethod
    def analyze_tree_structure(root: Optional[BSTNode[T]]) -> Dict[str, Any]:
        """Analyze the structure of a binary search tree."""
        if root is None:
            return {
                "type": "empty",
                "height": 0,
                "size": 0,
                "balance_factor": 0,
                "structure": "empty"
            }

        height = BSTAnalyzer._calculate_height(root)
        size = BSTAnalyzer._calculate_size(root)
        is_balanced = BSTAnalyzer._is_balanced(root)

        # Determine tree structure type
        if size == 1:
            structure = "single_node"
        elif height == size - 1:
            structure = "linear"  # Degenerate tree
        elif is_balanced and height <= 2 * (size ** 0.5):
            structure = "balanced"
        else:
            structure = "unbalanced"

        # Calculate balance factor
        left_height = BSTAnalyzer._calculate_height(root.left)
        right_height = BSTAnalyzer._calculate_height(root.right)
        balance_factor = right_height - left_height

        return {
            "type": "binary_search_tree",
            "height": height,
            "size": size,
            "balance_factor": balance_factor,
            "is_balanced": is_balanced,
            "structure": structure,
            "theoretical_min_height": int(size ** 0.5) if size > 0 else 0,
            "efficiency_ratio": height / (size ** 0.5) if size > 0 else 0
        }

    @staticmethod
    def get_tree_visualization(root: Optional[BSTNode[T]], max_depth: int = 5) -> str:
        """Generate a text-based visualization of the tree."""
        if root is None:
            return "Empty tree"

        def get_level_nodes(node: Optional[BSTNode[T]], level: int, max_level: int) -> List[Optional[BSTNode[T]]]:
            if level > max_level or node is None:
                return []

            if level == max_level:
                return [node]

            left_nodes = get_level_nodes(node.left, level + 1, max_level)
            right_nodes = get_level_nodes(node.right, level + 1, max_level)

            return left_nodes + right_nodes

        lines = []
        for level in range(min(max_depth, BSTAnalyzer._calculate_height(root) + 1)):
            nodes = get_level_nodes(root, 0, level)
            if not nodes:
                break

            # Create level line
            level_str = "  " * (2 ** (max_depth - level) - 1)
            node_strs = []

            for node in nodes:
                if node is None:
                    node_strs.append("  ")
                else:
                    node_strs.append(f"{node.value:2d}")

            level_str += " ".join(node_strs)
            lines.append(level_str)

        return "\n".join(lines)

    @staticmethod
    def memory_efficiency_analysis(root: Optional[BSTNode[T]]) -> Dict[str, Any]:
        """Analyze memory efficiency of a BST."""
        if root is None:
            return {
                "total_memory": 0,
                "node_memory": 0,
                "value_memory": 0,
                "overhead_memory": 0,
                "memory_per_node": 0,
                "efficiency_score": 0.0
            }

        total_memory = BSTAnalyzer._calculate_memory_usage(root)
        size = BSTAnalyzer._calculate_size(root)

        # Calculate memory breakdown
        node_memory = size * sys.getsizeof(BSTNode(0))  # Approximate node memory
        value_memory = BSTAnalyzer._calculate_value_memory(root)
        overhead_memory = total_memory - node_memory - value_memory

        memory_per_node = total_memory / size if size > 0 else 0

        # Calculate efficiency score (lower is better)
        theoretical_min_memory = size * (sys.getsizeof(0) + 24)  # Minimal possible memory
        efficiency_score = theoretical_min_memory / total_memory if total_memory > 0 else 0

        return {
            "total_memory": total_memory,
            "node_memory": node_memory,
            "value_memory": value_memory,
            "overhead_memory": overhead_memory,
            "memory_per_node": memory_per_node,
            "efficiency_score": efficiency_score,
            "theoretical_min_memory": theoretical_min_memory
        }

    @staticmethod
    def _calculate_value_memory(node: Optional[BSTNode[T]]) -> int:
        """Calculate memory used by values in a tree."""
        if node is None:
            return 0

        value_memory = sys.getsizeof(node.value)
        return value_memory + BSTAnalyzer._calculate_value_memory(node.left) + BSTAnalyzer._calculate_value_memory(node.right)

"""
BST Node implementation for Chapter 6.

This module provides the core node structure for Binary Search Trees.
"""

from typing import TypeVar, Generic, Optional
from dataclasses import dataclass

T = TypeVar('T')

@dataclass
class BSTNode(Generic[T]):
    """
    A node in a Binary Search Tree.

    Each node contains:
    - value: The data stored in the node
    - left: Reference to left child (smaller values)
    - right: Reference to right child (larger values)
    - parent: Reference to parent node (for efficient operations)
    """
    value: T
    left: Optional['BSTNode[T]'] = None
    right: Optional['BSTNode[T]'] = None
    parent: Optional['BSTNode[T]'] = None

    def __post_init__(self):
        """Update parent references of children."""
        if self.left:
            self.left.parent = self
        if self.right:
            self.right.parent = self

    def is_leaf(self) -> bool:
        """Check if this node is a leaf (no children)."""
        return self.left is None and self.right is None

    def has_one_child(self) -> bool:
        """Check if this node has exactly one child."""
        return (self.left is None) != (self.right is None)

    def get_only_child(self) -> Optional['BSTNode[T]']:
        """Get the only child if this node has exactly one child."""
        if self.left is not None and self.right is None:
            return self.left
        elif self.left is None and self.right is not None:
            return self.right
        return None

    def get_children_count(self) -> int:
        """Get the number of children this node has."""
        count = 0
        if self.left:
            count += 1
        if self.right:
            count += 1
        return count

    def is_left_child(self) -> bool:
        """Check if this node is a left child of its parent."""
        return self.parent is not None and self.parent.left == self

    def is_right_child(self) -> bool:
        """Check if this node is a right child of its parent."""
        return self.parent is not None and self.parent.right == self

    def get_sibling(self) -> Optional['BSTNode[T]']:
        """Get the sibling of this node."""
        if self.parent is None:
            return None

        if self.is_left_child():
            return self.parent.right
        else:
            return self.parent.left

    def __repr__(self) -> str:
        return f"BSTNode(value={self.value})"

"""
File System Tree implementation for Chapter 6.

This module demonstrates how BST principles can be applied to real-world
hierarchical data structures like file systems.
"""

import os
from typing import Optional, List, Dict, Any
from dataclasses import dataclass
from datetime import datetime

@dataclass
class FileNode:
    """Represents a file or directory in the file system."""
    name: str
    path: str
    is_directory: bool
    size: int
    modified_time: datetime
    children: Optional[List['FileNode']] = None

    def __post_init__(self):
        if self.children is None:
            self.children = []

    def get_total_size(self) -> int:
        """Get the total size including all children."""
        total = self.size
        for child in self.children:
            total += child.get_total_size()
        return total

    def get_file_count(self) -> int:
        """Get the number of files (not directories) in this subtree."""
        if not self.is_directory:
            return 1

        count = 0
        for child in self.children:
            count += child.get_file_count()
        return count

    def get_directory_count(self) -> int:
        """Get the number of directories in this subtree."""
        if not self.is_directory:
            return 0

        count = 1  # Count this directory
        for child in self.children:
            count += child.get_directory_count()
        return count

    def find_files_by_extension(self, extension: str) -> List['FileNode']:
        """Find all files with the given extension in this subtree."""
        result = []

        if not self.is_directory:
            if self.name.endswith(extension):
                result.append(self)
        else:
            for child in self.children:
                result.extend(child.find_files_by_extension(extension))

        return result

    def get_largest_files(self, count: int = 10) -> List['FileNode']:
        """Get the largest files in this subtree."""
        all_files = []
        self._collect_files(all_files)

        # Sort by size in descending order
        all_files.sort(key=lambda x: x.size, reverse=True)
        return all_files[:count]

    def _collect_files(self, files: List['FileNode']) -> None:
        """Collect all files in this subtree."""
        if not self.is_directory:
            files.append(self)
        else:
            for child in self.children:
                child._collect_files(files)

    def __repr__(self) -> str:
        node_type = "DIR" if self.is_directory else "FILE"
        return f"FileNode({self.name}, {node_type}, {self.size} bytes)"

class FileSystemTree:
    """
    A file system tree implementation using BST concepts.

    This demonstrates how BST principles can be applied to real-world
    hierarchical data structures.
    """

    def __init__(self, root_path: str):
        self.root_path = root_path
        self.root = self._build_tree(root_path)

    def _build_tree(self, path: str) -> Optional[FileNode]:
        """Build a tree representation of the file system."""
        if not os.path.exists(path):
            return None

        try:
            stat = os.stat(path)
            name = os.path.basename(path)
            is_directory = os.path.isdir(path)

            node = FileNode(
                name=name,
                path=path,
                is_directory=is_directory,
                size=stat.st_size,
                modified_time=datetime.fromtimestamp(stat.st_mtime)
            )

            if is_directory:
                try:
                    children = []
                    for child_name in sorted(os.listdir(path)):
                        child_path = os.path.join(path, child_name)
                        child_node = self._build_tree(child_path)
                        if child_node:
                            children.append(child_node)
                    node.children = children
                except PermissionError:
                    pass  # Skip directories we can't access

            return node
        except (PermissionError, OSError):
            return None

    def search_file(self, filename: str) -> List[FileNode]:
        """Search for files with a given name."""
        results = []
        self._search_recursive(self.root, filename, results)
        return results

    def _search_recursive(self, node: Optional[FileNode], filename: str, results: List[FileNode]) -> None:
        """Recursively search for files."""
        if node is None:
            return

        if node.name == filename:
            results.append(node)

        if node.children:
            for child in node.children:
                self._search_recursive(child, filename, results)

    def get_directory_size(self, path: str) -> int:
        """Calculate the total size of a directory."""
        node = self._find_node_by_path(path)
        if node is None:
            return 0
        return node.get_total_size()

    def _find_node_by_path(self, path: str) -> Optional[FileNode]:
        """Find a node by its path."""
        if self.root and self.root.path == path:
            return self.root

        def find_recursive(node: Optional[FileNode], target_path: str) -> Optional[FileNode]:
            if node is None:
                return None

            if node.path == target_path:
                return node

            if node.children:
                for child in node.children:
                    result = find_recursive(child, target_path)
                    if result:
                        return result

            return None

        return find_recursive(self.root, path)

    def list_files_by_extension(self, extension: str) -> List[FileNode]:
        """List all files with a given extension."""
        if self.root is None:
            return []
        return self.root.find_files_by_extension(extension)

    def get_largest_files(self, count: int = 10) -> List[FileNode]:
        """Get the largest files in the tree."""
        if self.root is None:
            return []
        return self.root.get_largest_files(count)

    def get_statistics(self) -> Dict[str, Any]:
        """Get comprehensive statistics about the file system tree."""
        if self.root is None:
            return {
                "total_size": 0,
                "file_count": 0,
                "directory_count": 0,
                "total_items": 0,
                "average_file_size": 0,
                "largest_file": None,
                "smallest_file": None
            }

        total_size = self.root.get_total_size()
        file_count = self.root.get_file_count()
        directory_count = self.root.get_directory_count()
        total_items = file_count + directory_count

        # Find largest and smallest files
        all_files = []
        self.root._collect_files(all_files)

        largest_file = max(all_files, key=lambda x: x.size) if all_files else None
        smallest_file = min(all_files, key=lambda x: x.size) if all_files else None

        average_file_size = total_size / file_count if file_count > 0 else 0

        return {
            "total_size": total_size,
            "file_count": file_count,
            "directory_count": directory_count,
            "total_items": total_items,
            "average_file_size": average_file_size,
            "largest_file": largest_file,
            "smallest_file": smallest_file
        }

    def print_tree(self, node: Optional[FileNode] = None, prefix: str = "", is_last: bool = True) -> None:
        """Print a tree representation of the file system."""
        if node is None:
            node = self.root
            if node is None:
                print("Empty tree")
                return

        # Print current node
        connector = "â””â”€â”€ " if is_last else "â”œâ”€â”€ "
        size_str = f" ({self._format_size(node.size)})" if not node.is_directory else "/"
        print(f"{prefix}{connector}{node.name}{size_str}")

        # Print children
        if node.children:
            new_prefix = prefix + ("    " if is_last else "â”‚   ")
            for i, child in enumerate(node.children):
                is_last_child = i == len(node.children) - 1
                self.print_tree(child, new_prefix, is_last_child)

    def _format_size(self, size: int) -> str:
        """Format file size in human-readable format."""
        for unit in ['B', 'KB', 'MB', 'GB']:
            if size < 1024.0:
                return f"{size:.1f} {unit}"
            size /= 1024.0
        return f"{size:.1f} TB"

    def get_directory_structure(self, max_depth: int = 3) -> Dict[str, Any]:
        """Get a structured representation of the directory hierarchy."""
        if self.root is None:
            return {}

        def build_structure(node: FileNode, depth: int) -> Dict[str, Any]:
            if depth > max_depth:
                return {"type": "truncated", "name": node.name}

            structure = {
                "name": node.name,
                "type": "directory" if node.is_directory else "file",
                "size": node.size,
                "modified_time": node.modified_time.isoformat()
            }

            if node.is_directory and node.children:
                structure["children"] = [
                    build_structure(child, depth + 1) 
                    for child in node.children
                ]

            return structure

        return build_structure(self.root, 0)

    def find_duplicate_files(self) -> Dict[str, List[FileNode]]:
        """Find files with the same size (potential duplicates)."""
        if self.root is None:
            return {}

        all_files = []
        self.root._collect_files(all_files)

        # Group files by size
        size_groups = {}
        for file_node in all_files:
            size = file_node.size
            if size not in size_groups:
                size_groups[size] = []
            size_groups[size].append(file_node)

        # Return only groups with multiple files
        return {size: files for size, files in size_groups.items() if len(files) > 1}

    def get_oldest_files(self, count: int = 10) -> List[FileNode]:
        """Get the oldest files in the tree."""
        if self.root is None:
            return []

        all_files = []
        self.root._collect_files(all_files)

        # Sort by modification time (oldest first)
        all_files.sort(key=lambda x: x.modified_time)
        return all_files[:count]

    def get_newest_files(self, count: int = 10) -> List[FileNode]:
        """Get the newest files in the tree."""
        if self.root is None:
            return []

        all_files = []
        self.root._collect_files(all_files)

        # Sort by modification time (newest first)
        all_files.sort(key=lambda x: x.modified_time, reverse=True)
        return all_files[:count]

    def search_by_pattern(self, pattern: str, case_sensitive: bool = True) -> List[FileNode]:
        """Search for files matching a pattern."""
        if self.root is None:
            return []

        results = []
        self._search_pattern_recursive(self.root, pattern, case_sensitive, results)
        return results

    def _search_pattern_recursive(self, node: FileNode, pattern: str, case_sensitive: bool, results: List[FileNode]) -> None:
        """Recursively search for files matching a pattern."""
        if not node.is_directory:
            name = node.name if case_sensitive else node.name.lower()
            pattern = pattern if case_sensitive else pattern.lower()

            if pattern in name:
                results.append(node)

        if node.children:
            for child in node.children:
                self._search_pattern_recursive(child, pattern, case_sensitive, results)

    def get_memory_usage(self) -> int:
        """Calculate the memory usage of this tree structure."""
        if self.root is None:
            return 0

        def calculate_node_memory(node: FileNode) -> int:
            # Approximate memory usage for a FileNode
            node_memory = (
                sys.getsizeof(node.name) +
                sys.getsizeof(node.path) +
                sys.getsizeof(node.size) +
                sys.getsizeof(node.modified_time) +
                sys.getsizeof(node.children)
            )

            # Add memory for children
            for child in node.children:
                node_memory += calculate_node_memory(child)

            return node_memory

        return calculate_node_memory(self.root)

"""
Iterative Binary Search Tree implementation for Chapter 6.

This module provides a BST implementation using iterative algorithms.
"""

from typing import TypeVar, Generic, Optional, Iterator, List
from collections import deque
from .bst_node import BSTNode

T = TypeVar('T')

class IterativeBST(Generic[T]):
    """
    A Binary Search Tree implementation using iterative algorithms.

    This implementation provides the same functionality as RecursiveBST
    but uses iterative approaches for better performance and memory efficiency.
    """

    def __init__(self):
        self._root: Optional[BSTNode[T]] = None
        self._size: int = 0

    def __len__(self) -> int:
        return self._size

    def is_empty(self) -> bool:
        return self._root is None

    def insert(self, value: T) -> None:
        """Insert a value into the BST iteratively."""
        if self._root is None:
            self._root = BSTNode(value)
            self._size = 1
            return

        current = self._root
        while True:
            if value < current.value:
                if current.left is None:
                    current.left = BSTNode(value, parent=current)
                    self._size += 1
                    break
                current = current.left
            else:  # value >= current.value
                if current.right is None:
                    current.right = BSTNode(value, parent=current)
                    self._size += 1
                    break
                current = current.right

    def search(self, value: T) -> Optional[BSTNode[T]]:
        """Search for a value in the BST iteratively."""
        current = self._root
        while current and current.value != value:
            if value < current.value:
                current = current.left
            else:
                current = current.right
        return current

    def contains(self, value: T) -> bool:
        """Check if a value exists in the BST."""
        return self.search(value) is not None

    def delete(self, value: T) -> bool:
        """Delete a value from the BST iteratively."""
        node = self.search(value)
        if node is None:
            return False

        self._delete_node(node)
        self._size -= 1
        return True

    def _delete_node(self, node: BSTNode[T]) -> None:
        """Delete a node from the BST."""
        if node.is_leaf():
            self._delete_leaf(node)
        elif node.has_one_child():
            self._delete_node_with_one_child(node)
        else:
            self._delete_node_with_two_children(node)

    def _delete_leaf(self, node: BSTNode[T]) -> None:
        """Delete a leaf node."""
        if node.parent is None:
            self._root = None
        elif node.parent.left == node:
            node.parent.left = None
        else:
            node.parent.right = None

    def _delete_node_with_one_child(self, node: BSTNode[T]) -> None:
        """Delete a node with exactly one child."""
        child = node.get_only_child()
        if node.parent is None:
            self._root = child
            if child:
                child.parent = None
        elif node.parent.left == node:
            node.parent.left = child
            if child:
                child.parent = node.parent
        else:
            node.parent.right = child
            if child:
                child.parent = node.parent

    def _delete_node_with_two_children(self, node: BSTNode[T]) -> None:
        """Delete a node with two children using successor."""
        successor = self._find_successor(node)
        if successor:
            node.value = successor.value
            self._delete_node(successor)

    def _find_successor(self, node: BSTNode[T]) -> Optional[BSTNode[T]]:
        """Find the successor of a node iteratively."""
        if node.right:
            return self._find_minimum(node.right)

        # Find the lowest ancestor whose left child is also an ancestor
        current = node
        while current.parent and current.parent.right == current:
            current = current.parent
        return current.parent

    def _find_predecessor(self, node: BSTNode[T]) -> Optional[BSTNode[T]]:
        """Find the predecessor of a node iteratively."""
        if node.left:
            return self._find_maximum(node.left)

        # Find the lowest ancestor whose right child is also an ancestor
        current = node
        while current.parent and current.parent.left == current:
            current = current.parent
        return current.parent

    def _find_minimum(self, node: BSTNode[T]) -> BSTNode[T]:
        """Find the minimum value in the subtree rooted at node."""
        while node.left:
            node = node.left
        return node

    def _find_maximum(self, node: BSTNode[T]) -> BSTNode[T]:
        """Find the maximum value in the subtree rooted at node."""
        while node.right:
            node = node.right
        return node

    def find_minimum(self) -> Optional[T]:
        """Find the minimum value in the BST."""
        if self._root is None:
            return None
        return self._find_minimum(self._root).value

    def find_maximum(self) -> Optional[T]:
        """Find the maximum value in the BST."""
        if self._root is None:
            return None
        return self._find_maximum(self._root).value

    def get_successor(self, value: T) -> Optional[T]:
        """Get the successor of a value in the BST."""
        node = self.search(value)
        if node is None:
            return None

        successor = self._find_successor(node)
        return successor.value if successor else None

    def get_predecessor(self, value: T) -> Optional[T]:
        """Get the predecessor of a value in the BST."""
        node = self.search(value)
        if node is None:
            return None

        predecessor = self._find_predecessor(node)
        return predecessor.value if predecessor else None

    def inorder_traversal(self) -> Iterator[T]:
        """Perform inorder traversal iteratively using a stack."""
        if self._root is None:
            return

        stack = []
        current = self._root

        while current or stack:
            # Reach the leftmost node
            while current:
                stack.append(current)
                current = current.left

            # Process current node
            current = stack.pop()
            yield current.value

            # Move to right subtree
            current = current.right

    def preorder_traversal(self) -> Iterator[T]:
        """Perform preorder traversal iteratively using a stack."""
        if self._root is None:
            return

        stack = [self._root]

        while stack:
            current = stack.pop()
            yield current.value

            # Push right child first (so left is processed first)
            if current.right:
                stack.append(current.right)
            if current.left:
                stack.append(current.left)

    def postorder_traversal(self) -> Iterator[T]:
        """Perform postorder traversal iteratively using two stacks."""
        if self._root is None:
            return

        stack1 = [self._root]
        stack2 = []

        while stack1:
            current = stack1.pop()
            stack2.append(current)

            if current.left:
                stack1.append(current.left)
            if current.right:
                stack1.append(current.right)

        while stack2:
            yield stack2.pop().value

    def level_order_traversal(self) -> Iterator[T]:
        """Perform level-order traversal using a queue."""
        if self._root is None:
            return

        queue = deque([self._root])

        while queue:
            node = queue.popleft()
            yield node.value

            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

    def get_sorted_list(self) -> List[T]:
        """Get all values in sorted order."""
        return list(self.inorder_traversal())

    def range_search(self, min_val: T, max_val: T) -> List[T]:
        """Get all values in the range [min_val, max_val]."""
        result = []
        stack = []
        current = self._root

        while current or stack:
            # Reach the leftmost node
            while current:
                stack.append(current)
                current = current.left

            # Process current node
            current = stack.pop()

            # If current node is in range, add it to result
            if min_val <= current.value <= max_val:
                result.append(current.value)
            elif current.value > max_val:
                break  # No need to check further

            # Move to right subtree
            current = current.right

        return result

    def get_height(self) -> int:
        """Get the height of the tree iteratively."""
        if self._root is None:
            return -1

        queue = deque([(self._root, 0)])
        max_height = 0

        while queue:
            node, height = queue.popleft()
            max_height = max(max_height, height)

            if node.left:
                queue.append((node.left, height + 1))
            if node.right:
                queue.append((node.right, height + 1))

        return max_height

    def is_balanced(self) -> bool:
        """Check if the tree is balanced iteratively."""
        if self._root is None:
            return True

        stack = [(self._root, False)]
        heights = {}

        while stack:
            node, visited = stack.pop()

            if visited:
                left_height = heights.get(id(node.left), -1)
                right_height = heights.get(id(node.right), -1)

                if abs(left_height - right_height) > 1:
                    return False

                heights[id(node)] = 1 + max(left_height, right_height)
            else:
                stack.append((node, True))
                if node.right:
                    stack.append((node.right, False))
                if node.left:
                    stack.append((node.left, False))

        return True

    def get_node_count(self) -> int:
        """Get the number of nodes in the tree iteratively."""
        if self._root is None:
            return 0

        count = 0
        stack = [self._root]

        while stack:
            node = stack.pop()
            count += 1

            if node.right:
                stack.append(node.right)
            if node.left:
                stack.append(node.left)

        return count

    def get_leaf_count(self) -> int:
        """Get the number of leaf nodes in the tree iteratively."""
        if self._root is None:
            return 0

        count = 0
        stack = [self._root]

        while stack:
            node = stack.pop()

            if node.is_leaf():
                count += 1
            else:
                if node.right:
                    stack.append(node.right)
                if node.left:
                    stack.append(node.left)

        return count

    def get_internal_node_count(self) -> int:
        """Get the number of internal nodes (non-leaf nodes) in the tree."""
        return self.get_node_count() - self.get_leaf_count()

    def clear(self) -> None:
        """Clear all elements from the tree."""
        self._root = None
        self._size = 0

    def __repr__(self) -> str:
        if self._root is None:
            return "IterativeBST()"

        values = list(self.inorder_traversal())
        return f"IterativeBST({values})"

"""
Recursive Binary Search Tree implementation for Chapter 6.

This module provides a BST implementation using recursive algorithms.
"""

from typing import TypeVar, Generic, Optional, Iterator, List
from .bst_node import BSTNode

T = TypeVar('T')

class RecursiveBST(Generic[T]):
    """
    A Binary Search Tree implementation using recursive algorithms.

    This implementation provides:
    - Efficient search, insert, and delete operations
    - Multiple traversal methods
    - Successor and predecessor operations
    - Memory-efficient node management
    """

    def __init__(self):
        self._root: Optional[BSTNode[T]] = None
        self._size: int = 0

    def __len__(self) -> int:
        return self._size

    def is_empty(self) -> bool:
        return self._root is None

    def insert(self, value: T) -> None:
        """Insert a value into the BST."""
        if self._root is None:
            self._root = BSTNode(value)
            self._size = 1
        else:
            self._insert_recursive(self._root, value)

    def _insert_recursive(self, node: BSTNode[T], value: T) -> None:
        """Recursively insert a value into the subtree rooted at node."""
        if value < node.value:
            if node.left is None:
                node.left = BSTNode(value, parent=node)
                self._size += 1
            else:
                self._insert_recursive(node.left, value)
        else:  # value >= node.value
            if node.right is None:
                node.right = BSTNode(value, parent=node)
                self._size += 1
            else:
                self._insert_recursive(node.right, value)

    def search(self, value: T) -> Optional[BSTNode[T]]:
        """Search for a value in the BST."""
        return self._search_recursive(self._root, value)

    def _search_recursive(self, node: Optional[BSTNode[T]], value: T) -> Optional[BSTNode[T]]:
        """Recursively search for a value in the subtree rooted at node."""
        if node is None or node.value == value:
            return node

        if value < node.value:
            return self._search_recursive(node.left, value)
        else:
            return self._search_recursive(node.right, value)

    def contains(self, value: T) -> bool:
        """Check if a value exists in the BST."""
        return self.search(value) is not None

    def delete(self, value: T) -> bool:
        """Delete a value from the BST."""
        node = self.search(value)
        if node is None:
            return False

        self._delete_node(node)
        self._size -= 1
        return True

    def _delete_node(self, node: BSTNode[T]) -> None:
        """Delete a node from the BST."""
        if node.is_leaf():
            self._delete_leaf(node)
        elif node.has_one_child():
            self._delete_node_with_one_child(node)
        else:
            self._delete_node_with_two_children(node)

    def _delete_leaf(self, node: BSTNode[T]) -> None:
        """Delete a leaf node."""
        if node.parent is None:
            self._root = None
        elif node.parent.left == node:
            node.parent.left = None
        else:
            node.parent.right = None

    def _delete_node_with_one_child(self, node: BSTNode[T]) -> None:
        """Delete a node with exactly one child."""
        child = node.get_only_child()
        if node.parent is None:
            self._root = child
            if child:
                child.parent = None
        elif node.parent.left == node:
            node.parent.left = child
            if child:
                child.parent = node.parent
        else:
            node.parent.right = child
            if child:
                child.parent = node.parent

    def _delete_node_with_two_children(self, node: BSTNode[T]) -> None:
        """Delete a node with two children using successor."""
        successor = self._find_successor(node)
        if successor:
            node.value = successor.value
            self._delete_node(successor)

    def _find_successor(self, node: BSTNode[T]) -> Optional[BSTNode[T]]:
        """Find the successor of a node."""
        if node.right:
            return self._find_minimum(node.right)

        # Find the lowest ancestor whose left child is also an ancestor
        current = node
        while current.parent and current.parent.right == current:
            current = current.parent
        return current.parent

    def _find_predecessor(self, node: BSTNode[T]) -> Optional[BSTNode[T]]:
        """Find the predecessor of a node."""
        if node.left:
            return self._find_maximum(node.left)

        # Find the lowest ancestor whose right child is also an ancestor
        current = node
        while current.parent and current.parent.left == current:
            current = current.parent
        return current.parent

    def _find_minimum(self, node: BSTNode[T]) -> BSTNode[T]:
        """Find the minimum value in the subtree rooted at node."""
        while node.left:
            node = node.left
        return node

    def _find_maximum(self, node: BSTNode[T]) -> BSTNode[T]:
        """Find the maximum value in the subtree rooted at node."""
        while node.right:
            node = node.right
        return node

    def find_minimum(self) -> Optional[T]:
        """Find the minimum value in the BST."""
        if self._root is None:
            return None
        return self._find_minimum(self._root).value

    def find_maximum(self) -> Optional[T]:
        """Find the maximum value in the BST."""
        if self._root is None:
            return None
        return self._find_maximum(self._root).value

    def get_successor(self, value: T) -> Optional[T]:
        """Get the successor of a value in the BST."""
        node = self.search(value)
        if node is None:
            return None

        successor = self._find_successor(node)
        return successor.value if successor else None

    def get_predecessor(self, value: T) -> Optional[T]:
        """Get the predecessor of a value in the BST."""
        node = self.search(value)
        if node is None:
            return None

        predecessor = self._find_predecessor(node)
        return predecessor.value if predecessor else None

    def inorder_traversal(self) -> Iterator[T]:
        """Perform inorder traversal (Left â†’ Root â†’ Right)."""
        def inorder_recursive(node: Optional[BSTNode[T]]) -> Iterator[T]:
            if node:
                yield from inorder_recursive(node.left)
                yield node.value
                yield from inorder_recursive(node.right)

        yield from inorder_recursive(self._root)

    def preorder_traversal(self) -> Iterator[T]:
        """Perform preorder traversal (Root â†’ Left â†’ Right)."""
        def preorder_recursive(node: Optional[BSTNode[T]]) -> Iterator[T]:
            if node:
                yield node.value
                yield from preorder_recursive(node.left)
                yield from preorder_recursive(node.right)

        yield from preorder_recursive(self._root)

    def postorder_traversal(self) -> Iterator[T]:
        """Perform postorder traversal (Left â†’ Right â†’ Root)."""
        def postorder_recursive(node: Optional[BSTNode[T]]) -> Iterator[T]:
            if node:
                yield from postorder_recursive(node.left)
                yield from postorder_recursive(node.right)
                yield node.value

        yield from postorder_recursive(self._root)

    def level_order_traversal(self) -> Iterator[T]:
        """Perform level-order traversal (breadth-first)."""
        if self._root is None:
            return

        from collections import deque
        queue = deque([self._root])

        while queue:
            node = queue.popleft()
            yield node.value

            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

    def get_sorted_list(self) -> List[T]:
        """Get all values in sorted order."""
        return list(self.inorder_traversal())

    def range_search(self, min_val: T, max_val: T) -> List[T]:
        """Get all values in the range [min_val, max_val]."""
        result = []
        self._range_search_recursive(self._root, min_val, max_val, result)
        return result

    def _range_search_recursive(self, node: Optional[BSTNode[T]], min_val: T, max_val: T, result: List[T]) -> None:
        """Recursively search for values in the given range."""
        if node is None:
            return

        # If current node is greater than min_val, search left subtree
        if min_val < node.value:
            self._range_search_recursive(node.left, min_val, max_val, result)

        # If current node is in range, add it to result
        if min_val <= node.value <= max_val:
            result.append(node.value)

        # If current node is less than max_val, search right subtree
        if node.value < max_val:
            self._range_search_recursive(node.right, min_val, max_val, result)

    def get_height(self) -> int:
        """Get the height of the tree."""
        return self._get_height_recursive(self._root)

    def _get_height_recursive(self, node: Optional[BSTNode[T]]) -> int:
        """Recursively calculate the height of a subtree."""
        if node is None:
            return -1
        return 1 + max(
            self._get_height_recursive(node.left),
            self._get_height_recursive(node.right)
        )

    def is_balanced(self) -> bool:
        """Check if the tree is balanced."""
        return self._is_balanced_recursive(self._root) != -1

    def _is_balanced_recursive(self, node: Optional[BSTNode[T]]) -> int:
        """Recursively check if a subtree is balanced. Returns -1 if unbalanced."""
        if node is None:
            return 0

        left_height = self._is_balanced_recursive(node.left)
        if left_height == -1:
            return -1

        right_height = self._is_balanced_recursive(node.right)
        if right_height == -1:
            return -1

        if abs(left_height - right_height) > 1:
            return -1

        return 1 + max(left_height, right_height)

    def clear(self) -> None:
        """Clear all elements from the tree."""
        self._root = None
        self._size = 0

    def __repr__(self) -> str:
        if self._root is None:
            return "RecursiveBST()"

        values = list(self.inorder_traversal())
        return f"RecursiveBST({values})"

"""
AVL Tree Implementation

This module provides a complete AVL tree implementation with automatic balancing,
including all standard BST operations with guaranteed O(log n) performance.
"""

from typing import TypeVar, Generic, Optional, Iterator, List
from .avl_node import AVLNode

T = TypeVar('T')

class AVLTree(Generic[T]):
    """
    An AVL Tree implementation with automatic balancing.

    This implementation provides:
    - Guaranteed O(log n) operations through automatic balancing
    - Four types of rotations to maintain AVL property
    - Efficient height and balance factor calculations
    - All standard BST operations with improved worst-case performance
    """

    def __init__(self):
        self._root: Optional[AVLNode[T]] = None
        self._size: int = 0

    def __len__(self) -> int:
        return self._size

    def is_empty(self) -> bool:
        return self._root is None

    def height(self) -> int:
        """Get the height of the tree."""
        return self._root.height if self._root else 0

    def insert(self, value: T) -> None:
        """Insert a value into the AVL tree and rebalance if necessary."""
        if self._root is None:
            self._root = AVLNode(value)
            self._size = 1
        else:
            self._root = self._insert_recursive(self._root, value)
            self._size += 1

    def _insert_recursive(self, node: AVLNode[T], value: T) -> AVLNode[T]:
        """Recursively insert a value and rebalance the tree."""
        if value < node.value:
            if node.left is None:
                node.left = AVLNode(value, parent=node)
            else:
                node.left = self._insert_recursive(node.left, value)
        else:  # value >= node.value
            if node.right is None:
                node.right = AVLNode(value, parent=node)
            else:
                node.right = self._insert_recursive(node.right, value)

        # Update height and rebalance
        node.update_height()
        return self._rebalance(node)

    def delete(self, value: T) -> bool:
        """Delete a value from the AVL tree and rebalance if necessary."""
        if self._root is None:
            return False

        # Check if the value exists before deleting
        if self.search(value) is None:
            return False

        self._root = self._delete_recursive(self._root, value)
        return True

    def _delete_recursive(self, node: Optional[AVLNode[T]], value: T) -> Optional[AVLNode[T]]:
        """Recursively delete a value and rebalance the tree."""
        if node is None:
            return None

        if value < node.value:
            node.left = self._delete_recursive(node.left, value)
        elif value > node.value:
            node.right = self._delete_recursive(node.right, value)
        else:
            # Node to delete found
            self._size -= 1

            # Case 1: Node is a leaf
            if node.is_leaf():
                return None

            # Case 2: Node has one child
            elif node.has_one_child():
                child = node.get_only_child()
                if child:
                    child.parent = node.parent
                return child

            # Case 3: Node has two children
            else:
                # Find successor (smallest value in right subtree)
                successor = self._find_min(node.right)
                if successor:
                    # Copy successor's value to current node
                    node.value = successor.value
                    # Delete successor (don't decrement size again)
                    node.right = self._delete_recursive(node.right, successor.value)
                    # Increment size back since we're not actually deleting a node
                    self._size += 1

        # Update height and rebalance
        if node:
            node.update_height()
            return self._rebalance(node)
        return None

    def search(self, value: T) -> Optional[AVLNode[T]]:
        """Search for a value in the AVL tree."""
        return self._search_recursive(self._root, value)

    def _search_recursive(self, node: Optional[AVLNode[T]], value: T) -> Optional[AVLNode[T]]:
        """Recursively search for a value in the tree."""
        if node is None or node.value == value:
            return node

        if value < node.value:
            return self._search_recursive(node.left, value)
        else:
            return self._search_recursive(node.right, value)

    def _find_min(self, node: Optional[AVLNode[T]]) -> Optional[AVLNode[T]]:
        """Find the minimum value in the subtree rooted at node."""
        if node is None:
            return None

        while node.left is not None:
            node = node.left
        return node

    def _find_max(self, node: Optional[AVLNode[T]]) -> Optional[AVLNode[T]]:
        """Find the maximum value in the subtree rooted at node."""
        if node is None:
            return None

        while node.right is not None:
            node = node.right
        return node

    def _rebalance(self, node: AVLNode[T]) -> AVLNode[T]:
        """Rebalance the tree starting from the given node."""
        balance_factor = node.get_balance_factor()

        # Left heavy
        if balance_factor < -1:
            # Left-Right case
            if node.left and node.left.get_balance_factor() > 0:
                node.left = self._left_rotate(node.left)
            # Left-Left case
            return self._right_rotate(node)

        # Right heavy
        elif balance_factor > 1:
            # Right-Left case
            if node.right and node.right.get_balance_factor() < 0:
                node.right = self._right_rotate(node.right)
            # Right-Right case
            return self._left_rotate(node)

        return node

    def _left_rotate(self, node: AVLNode[T]) -> AVLNode[T]:
        """Perform a left rotation on the given node."""
        right_child = node.right
        if right_child is None:
            return node

        # Perform rotation
        node.right = right_child.left
        if right_child.left:
            right_child.left.parent = node

        right_child.left = node
        right_child.parent = node.parent
        node.parent = right_child

        # Update heights
        node.update_height()
        right_child.update_height()

        return right_child

    def _right_rotate(self, node: AVLNode[T]) -> AVLNode[T]:
        """Perform a right rotation on the given node."""
        left_child = node.left
        if left_child is None:
            return node

        # Perform rotation
        node.left = left_child.right
        if left_child.right:
            left_child.right.parent = node

        left_child.right = node
        left_child.parent = node.parent
        node.parent = left_child

        # Update heights
        node.update_height()
        left_child.update_height()

        return left_child

    def inorder_traversal(self) -> Iterator[T]:
        """Perform inorder traversal of the tree."""
        def _inorder(node: Optional[AVLNode[T]]) -> Iterator[T]:
            if node:
                yield from _inorder(node.left)
                yield node.value
                yield from _inorder(node.right)

        yield from _inorder(self._root)

    def preorder_traversal(self) -> Iterator[T]:
        """Perform preorder traversal of the tree."""
        def _preorder(node: Optional[AVLNode[T]]) -> Iterator[T]:
            if node:
                yield node.value
                yield from _preorder(node.left)
                yield from _preorder(node.right)

        yield from _preorder(self._root)

    def postorder_traversal(self) -> Iterator[T]:
        """Perform postorder traversal of the tree."""
        def _postorder(node: Optional[AVLNode[T]]) -> Iterator[T]:
            if node:
                yield from _postorder(node.left)
                yield from _postorder(node.right)
                yield node.value

        yield from _postorder(self._root)

    def level_order_traversal(self) -> Iterator[List[T]]:
        """Perform level-order traversal of the tree."""
        if self._root is None:
            return

        queue = [self._root]
        while queue:
            level_size = len(queue)
            level_values = []

            for _ in range(level_size):
                node = queue.pop(0)
                level_values.append(node.value)

                if node.left:
                    queue.append(node.left)
                if node.right:
                    queue.append(node.right)

            yield level_values

    def get_sorted_values(self) -> List[T]:
        """Get all values in sorted order."""
        return list(self.inorder_traversal())

    def successor(self, value: T) -> Optional[T]:
        """Find the successor of the given value."""
        node = self.search(value)
        if node is None:
            return None

        # If node has right child, successor is minimum of right subtree
        if node.right:
            successor_node = self._find_min(node.right)
            return successor_node.value if successor_node else None

        # Otherwise, successor is the first ancestor where we turn right
        current = node
        while current.parent and current == current.parent.right:
            current = current.parent

        return current.parent.value if current.parent else None

    def predecessor(self, value: T) -> Optional[T]:
        """Find the predecessor of the given value."""
        node = self.search(value)
        if node is None:
            return None

        # If node has left child, predecessor is maximum of left subtree
        if node.left:
            predecessor_node = self._find_max(node.left)
            return predecessor_node.value if predecessor_node else None

        # Otherwise, predecessor is the first ancestor where we turn left
        current = node
        while current.parent and current == current.parent.left:
            current = current.parent

        return current.parent.value if current.parent else None

    def is_balanced(self) -> bool:
        """Check if the tree satisfies the AVL property."""
        def _check_balance(node: Optional[AVLNode[T]]) -> bool:
            if node is None:
                return True

            balance_factor = node.get_balance_factor()
            if abs(balance_factor) > 1:
                return False

            return _check_balance(node.left) and _check_balance(node.right)

        return _check_balance(self._root)

    def __repr__(self) -> str:
        return f"AVLTree({self.get_sorted_values()})"

"""
Database Index Implementation using AVL Trees

This module demonstrates how AVL trees can be used in real-world applications
for efficient data retrieval and range queries.
"""

from typing import Dict, List, Optional, Tuple, Any
import json
from .avl_tree import AVLTree, AVLNode

class DatabaseIndex:
    """
    A simple database index implementation using AVL trees.

    This demonstrates how AVL trees can be used in real-world applications
    for efficient data retrieval and range queries.
    """

    def __init__(self):
        self._index: AVLTree[Tuple[str, Any, int]] = AVLTree()
        self._data: Dict[int, Dict[str, Any]] = {}
        self._next_id: int = 1

    def insert_record(self, record: Dict[str, Any]) -> int:
        """Insert a record and update the index."""
        record_id = self._next_id
        self._next_id += 1

        # Store the record
        self._data[record_id] = record.copy()

        # Update index for each searchable field
        for field_name, field_value in record.items():
            if isinstance(field_value, (str, int, float)):
                index_key = (field_name, field_value, record_id)
                self._index.insert(index_key)

        return record_id

    def search_by_field(self, field_name: str, field_value: Any) -> List[Dict[str, Any]]:
        """Search for records by a specific field value."""
        results = []

        # Traverse all index entries and find matches
        for index_key in self._index.inorder_traversal():
            node_field, node_value, node_id = index_key

            if node_field == field_name and node_value == field_value:
                if node_id in self._data:
                    results.append(self._data[node_id])

        return results

    def range_query(self, field_name: str, min_value: Any, max_value: Any) -> List[Dict[str, Any]]:
        """Search for records within a range of field values."""
        results = []

        # Traverse all index entries and find matches
        for index_key in self._index.inorder_traversal():
            node_field, node_value, node_id = index_key

            if node_field == field_name and min_value <= node_value <= max_value:
                if node_id in self._data:
                    results.append(self._data[node_id])

        return results

    def delete_record(self, record_id: int) -> bool:
        """Delete a record and update the index."""
        if record_id not in self._data:
            return False

        record = self._data[record_id]

        # Remove index entries
        for field_name, field_value in record.items():
            if isinstance(field_value, (str, int, float)):
                index_key = (field_name, field_value, record_id)
                self._index.delete(index_key)

        # Remove the record
        del self._data[record_id]
        return True

    def get_all_records(self) -> List[Dict[str, Any]]:
        """Get all records in the database."""
        return list(self._data.values())

    def get_index_stats(self) -> Dict[str, Any]:
        """Get statistics about the index."""
        return {
            'total_records': len(self._data),
            'index_size': len(self._index),
            'index_height': self._index.height(),
            'is_balanced': self._index.is_balanced()
        }

    def export_to_json(self, filename: str) -> None:
        """Export the database to a JSON file."""
        data = {
            'records': self._data,
            'next_id': self._next_id
        }
        with open(filename, 'w') as f:
            json.dump(data, f, indent=2)

    def import_from_json(self, filename: str) -> None:
        """Import the database from a JSON file."""
        with open(filename, 'r') as f:
            data = json.load(f)

        # Convert string keys back to integers
        self._data = {int(k): v for k, v in data['records'].items()}
        self._next_id = data['next_id']

        # Rebuild index
        self._index = AVLTree()
        for record_id, record in self._data.items():
            for field_name, field_value in record.items():
                if isinstance(field_value, (str, int, float)):
                    index_key = (field_name, field_value, record_id)
                    self._index.insert(index_key)

    def get_field_values(self, field_name: str) -> List[Any]:
        """Get all unique values for a specific field."""
        values = set()

        # Traverse all index entries and collect values for the field
        for index_key in self._index.inorder_traversal():
            node_field, node_value, _ = index_key

            if node_field == field_name:
                values.add(node_value)

        return sorted(list(values))

    def get_field_statistics(self, field_name: str) -> Dict[str, Any]:
        """Get statistics for a specific field."""
        values = self.get_field_values(field_name)

        if not values:
            return {
                'field_name': field_name,
                'count': 0,
                'min': None,
                'max': None,
                'unique_values': 0
            }

        # Count total records with this field
        total_count = 0
        for record in self._data.values():
            if field_name in record:
                total_count += 1

        numeric_values = [v for v in values if isinstance(v, (int, float))]

        stats = {
            'field_name': field_name,
            'count': total_count,
            'unique_values': len(set(values)),
            'min': min(values) if values else None,
            'max': max(values) if values else None
        }

        if numeric_values:
            stats['numeric_min'] = min(numeric_values)
            stats['numeric_max'] = max(numeric_values)
            # Count total records with a numeric value for this field
            numeric_count = 0
            for record in self._data.values():
                v = record.get(field_name, None)
                if isinstance(v, (int, float)):
                    numeric_count += 1
            stats['numeric_count'] = numeric_count

        return stats

"""
Real-World Applications of Red-Black Trees

This module demonstrates practical applications of Red-Black trees
in various domains including database indexing, priority queues,
compiler symbol tables, file system structures, and network routing.

Classes:
    - DatabaseIndex: Database indexing application
    - PriorityQueue: Priority queue implementation
    - SymbolTable: Symbol table for compilers
    - FileSystemTree: File system directory structure
    - NetworkRoutingTable: Network routing table implementation
"""

from typing import Optional, List, Tuple, Dict
from .red_black_tree import RedBlackTree

class DatabaseIndex:
    """
    A simple database index using Red-Black trees.

    This demonstrates how Red-Black trees can be used for efficient
    database indexing and range queries.
    """

    def __init__(self):
        self.index = RedBlackTree[int]()
        self.data = {}

    def insert(self, key: int, value: str) -> None:
        """Insert a key-value pair into the index."""
        self.index.insert(key)
        self.data[key] = value

    def search(self, key: int) -> Optional[str]:
        """Search for a value by key."""
        if self.index.search(key) is not None:
            return self.data.get(key)
        return None

    def range_query(self, start: int, end: int) -> List[Tuple[int, str]]:
        """Perform a range query."""
        result = []
        for key in self.index.inorder_traversal():
            if start <= key <= end:
                result.append((key, self.data[key]))
        return result

    def delete(self, key: int) -> bool:
        """Delete a key-value pair."""
        if self.index.delete(key):
            del self.data[key]
            return True
        return False

class PriorityQueue:
    """
    A priority queue implementation using Red-Black trees.

    This provides O(log n) insertion and deletion operations,
    making it suitable for applications requiring efficient
    priority-based scheduling.
    """

    def __init__(self):
        self.tree = RedBlackTree[int]()
        self.items = {}  # Store actual items
        self.count = 0

    def enqueue(self, priority: int, item: str) -> None:
        """Add an item with given priority."""
        # Use priority as key, with count to handle duplicates
        key = priority * 1000000 + self.count
        self.tree.insert(key)
        self.items[key] = item  # Store the actual item
        self.count += 1

    def dequeue(self) -> Optional[str]:
        """Remove and return the highest priority item."""
        if self.tree.is_empty():
            return None

        min_key = self.tree.find_min()
        if min_key is not None:
            self.tree.delete(min_key)
            item = self.items.pop(min_key, None)
            return item
        return None

    def peek(self) -> Optional[str]:
        """Return the highest priority item without removing it."""
        if self.tree.is_empty():
            return None

        min_key = self.tree.find_min()
        if min_key is not None:
            return self.items.get(min_key, None)
        return None

    def is_empty(self) -> bool:
        """Check if the priority queue is empty."""
        return self.tree.is_empty()

    def size(self) -> int:
        """Get the size of the priority queue."""
        return len(self.tree)

class SymbolTable:
    """
    A symbol table implementation using Red-Black trees.

    This demonstrates how Red-Black trees can be used in compiler
    design for efficient symbol lookup and scoping.
    """

    def __init__(self):
        self.scopes = [RedBlackTree[str]()]
        self.symbol_data = [{}]  # Store actual symbol information
        self.current_scope = 0

    def enter_scope(self) -> None:
        """Enter a new scope."""
        self.scopes.append(RedBlackTree[str]())
        self.symbol_data.append({})
        self.current_scope += 1

    def exit_scope(self) -> None:
        """Exit the current scope."""
        if self.current_scope > 0:
            self.scopes.pop()
            self.symbol_data.pop()
            self.current_scope -= 1

    def insert(self, name: str, symbol_info: Dict) -> bool:
        """Insert a symbol into the current scope."""
        # Check if symbol already exists in current scope
        if self.scopes[self.current_scope].search(name) is not None:
            return False

        self.scopes[self.current_scope].insert(name)
        self.symbol_data[self.current_scope][name] = symbol_info
        return True

    def lookup(self, name: str) -> Optional[Dict]:
        """Look up a symbol starting from current scope."""
        for i in range(self.current_scope, -1, -1):
            if self.scopes[i].search(name) is not None:
                symbol_info = self.symbol_data[i].get(name, {})
                return {
                    "name": name,
                    "scope": i,
                    **symbol_info
                }
        return None

    def delete(self, name: str) -> bool:
        """Delete a symbol from the current scope."""
        if self.scopes[self.current_scope].delete(name):
            self.symbol_data[self.current_scope].pop(name, None)
            return True
        return False

    def get_all_symbols(self) -> List[str]:
        """Get all symbols in the current scope."""
        return list(self.scopes[self.current_scope].inorder_traversal())

class FileSystemTree:
    """
    A file system directory structure using Red-Black trees.

    This demonstrates how Red-Black trees can be used to represent
    hierarchical file system structures with efficient lookup and traversal.
    """

    def __init__(self):
        self.directories = RedBlackTree[str]()
        self.files = {}  # Store file metadata
        self.directory_contents = {}  # Store directory contents

    def create_directory(self, path: str) -> bool:
        """Create a new directory."""
        if path in self.directory_contents:
            return False

        parent_dir = self._get_parent_directory(path)
        if parent_dir:
            if parent_dir not in self.directory_contents:
                return False  # Parent directory must exist
            self.directory_contents[parent_dir].append(path.split('/')[-1])
        self.directories.insert(path)
        self.directory_contents[path] = []
        return True

    def create_file(self, path: str, size: int, metadata: Dict = None) -> bool:
        """Create a new file."""
        if path in self.files:
            return False

        parent_dir = self._get_parent_directory(path)
        if not parent_dir or parent_dir not in self.directory_contents:
            return False  # Parent directory must exist
        self.files[path] = {
            'size': size,
            'metadata': metadata or {},
            'parent': parent_dir
        }
        self.directory_contents[parent_dir].append(path)
        return True

    def _get_parent_directory(self, path: str) -> Optional[str]:
        """Get the parent directory of a path."""
        if '/' not in path:
            return None
        return '/'.join(path.split('/')[:-1])

    def list_directory(self, path: str) -> List[str]:
        """List contents of a directory."""
        return self.directory_contents.get(path, [])

    def get_file_info(self, path: str) -> Optional[Dict]:
        """Get file information."""
        return self.files.get(path)

    def delete_file(self, path: str) -> bool:
        """Delete a file."""
        if path not in self.files:
            return False

        parent_dir = self.files[path]['parent']
        if parent_dir in self.directory_contents:
            self.directory_contents[parent_dir].remove(path)
        del self.files[path]
        return True

    def delete_directory(self, path: str) -> bool:
        """Delete a directory and all its contents."""
        if path not in self.directory_contents:
            return False

        # Recursively delete all contents
        for item in list(self.directory_contents[path]):
            full_path = path + '/' + item if '/' not in item and not item.startswith(path + '/') else item
            if full_path in self.files:
                del self.files[full_path]
            elif full_path in self.directory_contents:
                self.delete_directory(full_path)

        # Remove this directory from its parent
        parent_dir = self._get_parent_directory(path)
        if parent_dir and parent_dir in self.directory_contents:
            name = path.split('/')[-1]
            if name in self.directory_contents[parent_dir]:
                self.directory_contents[parent_dir].remove(name)

        self.directories.delete(path)
        del self.directory_contents[path]
        return True

class NetworkRoutingTable:
    """
    A network routing table using Red-Black trees.

    This demonstrates how Red-Black trees can be used for efficient
    IP address lookup and routing decisions in network infrastructure.
    """

    def __init__(self):
        self.routes = RedBlackTree[str]()
        self.route_data = {}  # Store routing information

    def add_route(self, destination: str, next_hop: str, 
                  interface: str, metric: int = 1) -> bool:
        """Add a routing entry."""
        if destination in self.route_data:
            return False

        self.routes.insert(destination)
        self.route_data[destination] = {
            'next_hop': next_hop,
            'interface': interface,
            'metric': metric
        }
        return True

    def _is_prefix_match(self, route: str, destination: str) -> int:
        """Return the number of matching octets if route is a prefix of destination, else 0."""
        route_parts = route.split('.')
        dest_parts = destination.split('.')
        match_len = 0
        for r, d in zip(route_parts, dest_parts):
            if r == d:
                match_len += 1
            else:
                break
        return match_len if match_len == len(route_parts) else 0

    def lookup_route(self, destination: str) -> Optional[Dict]:
        """Look up the best route for a destination."""
        best_match = None
        best_length = 0
        for route in self.routes.inorder_traversal():
            match_len = self._is_prefix_match(route, destination)
            if match_len > best_length:
                best_length = match_len
                best_match = route
        if best_match:
            return self.route_data[best_match]
        return None

    def remove_route(self, destination: str) -> bool:
        """Remove a routing entry."""
        if destination not in self.route_data:
            return False

        self.routes.delete(destination)
        del self.route_data[destination]
        return True

    def get_all_routes(self) -> List[Tuple[str, Dict]]:
        """Get all routing entries."""
        return [(route, self.route_data[route]) 
                for route in self.routes.inorder_traversal()]

    def update_metric(self, destination: str, new_metric: int) -> bool:
        """Update the metric for a route."""
        if destination not in self.route_data:
            return False

        self.route_data[destination]['metric'] = new_metric
        return True

"""
Red-Black Tree Implementation

This module provides a complete implementation of Red-Black trees,
including the core data structure, performance analysis, and validation functions.

Classes:
    - Color: Enumeration for node colors
    - RedBlackNode: Node implementation for Red-Black trees
    - RedBlackTree: Complete Red-Black tree implementation

Functions:
    - red_black_height_analysis: Analyze height bounds
    - benchmark_red_black_tree_operations: Performance benchmarking
    - analyze_red_black_properties: Property validation
"""

from typing import TypeVar, Generic, Optional, Iterator, Dict
from enum import Enum
import timeit
import sys

T = TypeVar('T')

class Color(Enum):
    """Enumeration for node colors in Red-Black tree."""
    RED = "RED"
    BLACK = "BLACK"

class RedBlackNode(Generic[T]):
    """
    A node in a Red-Black tree.

    Each node contains:
    - key: The value stored in the node
    - color: RED or BLACK
    - left, right: References to child nodes
    - parent: Reference to parent node
    """

    def __init__(self, key: T, color: Color = Color.RED) -> None:
        self.key = key
        self.color = color
        self.left: Optional['RedBlackNode[T]'] = None
        self.right: Optional['RedBlackNode[T]'] = None
        self.parent: Optional['RedBlackNode[T]'] = None

    def __repr__(self) -> str:
        return f"RedBlackNode({self.key}, {self.color.value})"

    def is_red(self) -> bool:
        """Check if the node is red."""
        return self.color == Color.RED

    def is_black(self) -> bool:
        """Check if the node is black."""
        return self.color == Color.BLACK

    def set_red(self) -> None:
        """Set the node color to red."""
        self.color = Color.RED

    def set_black(self) -> None:
        """Set the node color to black."""
        self.color = Color.BLACK

    def get_sibling(self) -> Optional['RedBlackNode[T]']:
        """Get the sibling of this node."""
        if self.parent is None:
            return None

        if self.parent.left == self:
            return self.parent.right
        else:
            return self.parent.left

    def get_uncle(self) -> Optional['RedBlackNode[T]']:
        """Get the uncle of this node (parent's sibling)."""
        if self.parent is None or self.parent.parent is None:
            return None

        return self.parent.get_sibling()

class RedBlackTree(Generic[T]):
    """
    A Red-Black tree implementation.

    This tree maintains the five Red-Black properties:
    1. Every node is either red or black
    2. The root is always black
    3. All leaves (NIL) are black
    4. Red nodes cannot have red children
    5. Every path from root to leaves has the same number of black nodes

    This guarantees O(log n) performance for all operations.

    Thread Safety:
    This implementation is not thread-safe. For concurrent access,
    external synchronization (e.g., locks) must be used. Consider
    using threading.Lock or threading.RLock for multi-threaded applications.
    """

    def __init__(self) -> None:
        self.root: Optional[RedBlackNode[T]] = None
        self._size = 0

    def __len__(self) -> int:
        return self._size

    def is_empty(self) -> bool:
        """Check if the tree is empty."""
        return self.root is None

    def insert(self, key: T) -> None:
        """
        Insert a key into the Red-Black tree.

        Args:
            key: The key to insert

        Time Complexity: O(log n)
        """
        if key is None:
            raise ValueError("Cannot insert None key")

        node = RedBlackNode(key)

        # Perform standard BST insertion
        self._bst_insert(node)

        # Fix Red-Black properties
        self._fix_insert(node)

        self._size += 1

    def _bst_insert(self, node: RedBlackNode[T]) -> None:
        """Perform standard BST insertion."""
        if self.root is None:
            self.root = node
            return

        current = self.root
        parent = None

        while current is not None:
            parent = current
            if node.key < current.key:
                current = current.left
            else:
                current = current.right

        node.parent = parent
        if node.key < parent.key:
            parent.left = node
        else:
            parent.right = node

    def _fix_insert(self, node: RedBlackNode[T]) -> None:
        """Fix Red-Black properties after insertion."""
        # Case 1: node is root
        if node.parent is None:
            node.set_black()
            return

        # Case 2: parent is black
        if node.parent.is_black():
            return

        # Case 3: parent is red
        uncle = node.get_uncle()

        if uncle is not None and uncle.is_red():
            # Case 3a: uncle is red
            self._fix_red_uncle(node)
        else:
            # Case 3b: uncle is black or None
            self._fix_black_uncle(node)

    def _fix_red_uncle(self, node: RedBlackNode[T]) -> None:
        """Fix case where uncle is red."""
        parent = node.parent
        grandparent = parent.parent
        uncle = node.get_uncle()

        # Recolor
        parent.set_black()
        uncle.set_black()
        grandparent.set_red()

        # Recursively fix grandparent
        self._fix_insert(grandparent)

    def _fix_black_uncle(self, node: RedBlackNode[T]) -> None:
        """Fix case where uncle is black or None."""
        parent = node.parent
        grandparent = parent.parent

        # Determine if we need to rotate
        if parent == grandparent.left:
            if node == parent.right:
                # Left-Right case
                self._left_rotate(parent)
                node = parent
                parent = node.parent

            # Left-Left case
            self._right_rotate(grandparent)
        else:
            if node == parent.left:
                # Right-Left case
                self._right_rotate(parent)
                node = parent
                parent = node.parent

            # Right-Right case
            self._left_rotate(grandparent)

        # Recolor
        parent.set_black()
        grandparent.set_red()

    def _left_rotate(self, node: RedBlackNode[T]) -> None:
        """Perform left rotation around node."""
        right_child = node.right
        if right_child is None:
            return

        # Update parent pointers
        node.right = right_child.left
        if right_child.left is not None:
            right_child.left.parent = node

        right_child.parent = node.parent

        # Update root if necessary
        if node.parent is None:
            self.root = right_child
        elif node == node.parent.left:
            node.parent.left = right_child
        else:
            node.parent.right = right_child

        # Complete rotation
        right_child.left = node
        node.parent = right_child

    def _right_rotate(self, node: RedBlackNode[T]) -> None:
        """Perform right rotation around node."""
        left_child = node.left
        if left_child is None:
            return

        # Update parent pointers
        node.left = left_child.right
        if left_child.right is not None:
            left_child.right.parent = node

        left_child.parent = node.parent

        # Update root if necessary
        if node.parent is None:
            self.root = left_child
        elif node == node.parent.right:
            node.parent.right = left_child
        else:
            node.parent.left = left_child

        # Complete rotation
        left_child.right = node
        node.parent = left_child

    def search(self, key: T) -> Optional[RedBlackNode[T]]:
        """
        Search for a key in the Red-Black tree.

        Args:
            key: The key to search for

        Returns:
            The node containing the key, or None if not found

        Time Complexity: O(log n)
        """
        if key is None:
            return None

        return self._search_recursive(self.root, key)

    def _search_recursive(self, node: Optional[RedBlackNode[T]], key: T) -> Optional[RedBlackNode[T]]:
        """Recursively search for a key."""
        if node is None or node.key == key:
            return node

        if key < node.key:
            return self._search_recursive(node.left, key)
        else:
            return self._search_recursive(node.right, key)

    def delete(self, key: T) -> bool:
        """
        Delete a key from the Red-Black tree.

        Args:
            key: The key to delete

        Returns:
            True if the key was found and deleted, False otherwise

        Time Complexity: O(log n)
        """
        if key is None:
            return False

        node = self.search(key)
        if node is None:
            return False

        self._delete_node(node)
        self._size -= 1
        return True

    def _delete_node(self, node: RedBlackNode[T]) -> None:
        """Delete a node from the tree."""
        # Find the node to actually delete
        if node.left is not None and node.right is not None:
            # Node has two children - find successor
            successor = self._find_successor(node)
            node.key = successor.key
            node = successor

        # Node has at most one child
        child = node.left if node.left is not None else node.right

        if node.is_black():
            if child is None:
                # Node is black with no children
                self._fix_delete_double_black(node)
            else:
                # Node is black with one red child
                child.set_black()

        # Replace node with child
        if node.parent is None:
            self.root = child
        elif node == node.parent.left:
            node.parent.left = child
        else:
            node.parent.right = child

        if child is not None:
            child.parent = node.parent

    def _find_successor(self, node: RedBlackNode[T]) -> RedBlackNode[T]:
        """Find the successor of a node."""
        if node.right is not None:
            # Successor is the leftmost node in right subtree
            current = node.right
            while current.left is not None:
                current = current.left
            return current

        # Successor is the first ancestor whose left child is also an ancestor
        current = node
        while current.parent is not None and current == current.parent.right:
            current = current.parent
        return current.parent

    def _fix_delete_double_black(self, node: RedBlackNode[T]) -> None:
        """Fix double-black violation after deletion."""
        if node.parent is None:
            return

        sibling = node.get_sibling()
        if sibling is None:
            return

        if sibling.is_red():
            # Case 1: Sibling is red
            self._fix_red_sibling(node, sibling)
        else:
            # Case 2: Sibling is black
            self._fix_black_sibling(node, sibling)

    def _fix_red_sibling(self, node: RedBlackNode[T], sibling: RedBlackNode[T]) -> None:
        """Fix case where sibling is red."""
        parent = node.parent
        sibling.set_black()
        parent.set_red()

        if node == parent.left:
            self._left_rotate(parent)
        else:
            self._right_rotate(parent)

        # Continue fixing with new sibling
        new_sibling = node.get_sibling()
        if new_sibling is not None:
            self._fix_black_sibling(node, new_sibling)

    def _fix_black_sibling(self, node: RedBlackNode[T], sibling: RedBlackNode[T]) -> None:
        """Fix case where sibling is black."""
        parent = node.parent

        # Check if sibling's children are black
        left_nephew_black = sibling.left is None or sibling.left.is_black()
        right_nephew_black = sibling.right is None or sibling.right.is_black()

        if left_nephew_black and right_nephew_black:
            # Case 2a: Both nephews are black
            sibling.set_red()
            if parent.is_red():
                parent.set_black()
            else:
                self._fix_delete_double_black(parent)
        else:
            # Case 2b: At least one nephew is red
            self._fix_red_nephew(node, sibling, left_nephew_black, right_nephew_black)

    def _fix_red_nephew(self, node: RedBlackNode[T], sibling: RedBlackNode[T], 
                       left_nephew_black: bool, right_nephew_black: bool) -> None:
        """Fix case where at least one nephew is red."""
        parent = node.parent

        if node == parent.left:
            if right_nephew_black:
                # Case 2b(i): Right nephew is black, left nephew is red
                if sibling.left is not None:
                    sibling.left.set_black()
                sibling.set_red()
                self._right_rotate(sibling)
                sibling = parent.right

            # Case 2b(ii): Right nephew is red
            if sibling.right is not None:
                sibling.right.set_black()
            sibling.color = parent.color
            parent.set_black()
            self._left_rotate(parent)
        else:
            if left_nephew_black:
                # Case 2b(i): Left nephew is black, right nephew is red
                if sibling.right is not None:
                    sibling.right.set_black()
                sibling.set_red()
                self._left_rotate(sibling)
                sibling = parent.left

            # Case 2b(ii): Left nephew is red
            if sibling.left is not None:
                sibling.left.set_black()
            sibling.color = parent.color
            parent.set_black()
            self._right_rotate(parent)

    def find_min(self) -> Optional[T]:
        """Find the minimum key in the tree."""
        if self.root is None:
            return None

        current = self.root
        while current.left is not None:
            current = current.left
        return current.key

    def find_max(self) -> Optional[T]:
        """Find the maximum key in the tree."""
        if self.root is None:
            return None

        current = self.root
        while current.right is not None:
            current = current.right
        return current.key

    def inorder_traversal(self) -> Iterator[T]:
        """Perform inorder traversal of the tree."""
        def _inorder(node: Optional[RedBlackNode[T]]) -> Iterator[T]:
            if node is not None:
                yield from _inorder(node.left)
                yield node.key
                yield from _inorder(node.right)

        yield from _inorder(self.root)

    def preorder_traversal(self) -> Iterator[T]:
        """Perform preorder traversal of the tree."""
        def _preorder(node: Optional[RedBlackNode[T]]) -> Iterator[T]:
            if node is not None:
                yield node.key
                yield from _preorder(node.left)
                yield from _preorder(node.right)

        yield from _preorder(self.root)

    def postorder_traversal(self) -> Iterator[T]:
        """Perform postorder traversal of the tree."""
        def _postorder(node: Optional[RedBlackNode[T]]) -> Iterator[T]:
            if node is not None:
                yield from _postorder(node.left)
                yield from _postorder(node.right)
                yield node.key

        yield from _postorder(self.root)

    def level_order_traversal(self) -> Iterator[list[T]]:
        """Perform level-order traversal of the tree."""
        if self.root is None:
            return

        queue = [self.root]
        while queue:
            level_size = len(queue)
            level = []

            for _ in range(level_size):
                node = queue.pop(0)
                level.append(node.key)

                if node.left is not None:
                    queue.append(node.left)
                if node.right is not None:
                    queue.append(node.right)

            yield level

    def height(self) -> int:
        """Calculate the height of the tree."""
        def _height(node: Optional[RedBlackNode[T]]) -> int:
            if node is None:
                return -1  # Standard definition: empty tree has height -1
            return 1 + max(_height(node.left), _height(node.right))

        return _height(self.root)

    def black_height(self) -> int:
        """Calculate the black height of the tree."""
        if self.root is None:
            return 0

        def _black_height(node: Optional[RedBlackNode[T]]) -> int:
            if node is None:
                return 0  # Don't count NIL nodes
            left_bh = _black_height(node.left)
            right_bh = _black_height(node.right)
            if left_bh != right_bh:
                raise ValueError("Invalid Red-Black tree: unequal black heights")
            return left_bh + (1 if node.is_black() else 0)

        return _black_height(self.root)

    def is_valid(self) -> bool:
        """Check if the tree satisfies all Red-Black properties."""
        if self.root is None:
            return True

        # Property 2: Root is black
        if self.root.is_red():
            return False

        try:
            # Check all paths have same black height
            black_height = self._get_black_height(self.root)
            return self._check_properties(self.root, black_height, 0)
        except ValueError:
            # Black heights are unequal
            return False

    def _get_black_height(self, node: Optional[RedBlackNode[T]]) -> int:
        """Get the black height from a node to any leaf."""
        if node is None:
            return 0  # Don't count NIL nodes
        left_bh = self._get_black_height(node.left)
        right_bh = self._get_black_height(node.right)
        if left_bh != right_bh:
            raise ValueError("Invalid Red-Black tree: unequal black heights")
        return left_bh + (1 if node.is_black() else 0)

    def _check_properties(self, node: Optional[RedBlackNode[T]], 
                         expected_black_height: int, current_black_height: int) -> bool:
        """Check Red-Black properties recursively."""
        if node is None:
            return current_black_height == expected_black_height

        # Property 4: No two consecutive red nodes
        if node.is_red():
            if (node.left is not None and node.left.is_red()) or \
               (node.right is not None and node.right.is_red()):
                return False

        # Update black height
        new_black_height = current_black_height + (1 if node.is_black() else 0)

        # Check left and right subtrees
        return (self._check_properties(node.left, expected_black_height, new_black_height) and
                self._check_properties(node.right, expected_black_height, new_black_height))

    def __repr__(self) -> str:
        return f"RedBlackTree(size={self._size}, height={self.height()})"

def red_black_height_analysis(n: int) -> Dict[str, float]:
    """
    Analyze Red-Black tree height bounds.

    Args:
        n: Number of nodes in the Red-Black tree

    Returns:
        Dictionary containing height analysis
    """
    # Calculate theoretical bounds
    min_black_height = (n + 1).bit_length() - 1
    max_black_height = n  # Worst case: all nodes black in a chain

    # Red-Black height bound
    rb_height_bound = 2 * (n + 1).bit_length() - 2

    # AVL height bound for comparison
    avl_height_bound = int(1.44 * (n + 2).bit_length() - 0.328)

    # Perfect binary tree height
    perfect_height = (n + 1).bit_length() - 1

    return {
        'nodes': n,
        'min_black_height': min_black_height,
        'max_black_height': max_black_height,
        'rb_height_bound': rb_height_bound,
        'avl_height_bound': avl_height_bound,
        'perfect_height': perfect_height,
        'rb_vs_avl_ratio': rb_height_bound / avl_height_bound if avl_height_bound > 0 else 0,
        'rb_vs_perfect_ratio': rb_height_bound / perfect_height if perfect_height > 0 else 0
    }

def benchmark_red_black_tree_operations():
    """
    Benchmark Red-Black tree operations against built-in data structures.

    This demonstrates the performance characteristics of Red-Black trees
    and compares them with Python's built-in sorted data structures.
    """

    def benchmark_insertion():
        """Benchmark insertion operations."""
        print("=== Insertion Benchmark ===")

        # Test data
        test_sizes = [100, 1000, 10000]

        for size in test_sizes:
            print(f"\nSize: {size}")

            # Red-Black tree insertion
            rb_tree = RedBlackTree[int]()
            rb_time = timeit.timeit(
                lambda: [rb_tree.insert(i) for i in range(size)],
                number=1
            )
            print(f"Red-Black Tree: {rb_time:.6f}s")

            # List insertion (for comparison)
            list_time = timeit.timeit(
                lambda: [i for i in range(size)],
                number=1
            )
            print(f"List: {list_time:.6f}s")

            # Sorted list insertion
            sorted_list = []
            sorted_time = timeit.timeit(
                lambda: sorted_list.extend(range(size)),
                number=1
            )
            print(f"Sorted List: {sorted_time:.6f}s")

    def benchmark_search():
        """Benchmark search operations."""
        print("\n=== Search Benchmark ===")

        # Prepare test data
        size = 10000
        rb_tree = RedBlackTree[int]()
        for i in range(size):
            rb_tree.insert(i)

        # Red-Black tree search
        rb_time = timeit.timeit(
            lambda: [rb_tree.search(i) for i in range(0, size, 100)],
            number=100
        )
        print(f"Red-Black Tree Search: {rb_time:.6f}s")

        # List search (for comparison)
        test_list = list(range(size))
        list_time = timeit.timeit(
            lambda: [i in test_list for i in range(0, size, 100)],
            number=100
        )
        print(f"List Search: {list_time:.6f}s")

    def benchmark_deletion():
        """Benchmark deletion operations."""
        print("\n=== Deletion Benchmark ===")

        # Prepare test data
        size = 1000
        rb_tree = RedBlackTree[int]()
        for i in range(size):
            rb_tree.insert(i)

        # Red-Black tree deletion
        rb_time = timeit.timeit(
            lambda: [rb_tree.delete(i) for i in range(0, size, 2)],
            number=1
        )
        print(f"Red-Black Tree Deletion: {rb_time:.6f}s")

    def benchmark_memory_usage():
        """Benchmark memory usage."""
        print("\n=== Memory Usage Benchmark ===")

        import sys

        # Test different data structures
        test_size = 10000

        # Red-Black tree
        rb_tree = RedBlackTree[int]()
        for i in range(test_size):
            rb_tree.insert(i)
        rb_memory = sys.getsizeof(rb_tree) + sum(sys.getsizeof(node) for node in rb_tree.inorder_traversal())

        # List
        test_list = list(range(test_size))
        list_memory = sys.getsizeof(test_list) + sum(sys.getsizeof(i) for i in test_list)

        # Dictionary
        test_dict = {i: i for i in range(test_size)}
        dict_memory = sys.getsizeof(test_dict)

        print(f"Red-Black Tree Memory: {rb_memory:,} bytes")
        print(f"List Memory: {list_memory:,} bytes")
        print(f"Dictionary Memory: {dict_memory:,} bytes")
        print(f"RB Tree vs List ratio: {rb_memory/list_memory:.2f}")
        print(f"RB Tree vs Dict ratio: {rb_memory/dict_memory:.2f}")

    def compare_with_builtin_dict():
        """Compare Red-Black tree performance with dict."""
        print("\n=== Comparison with Built-in Dict ===")

        test_size = 10000

        # Red-Black tree operations
        rb_tree = RedBlackTree[int]()
        rb_insert_time = timeit.timeit(
            lambda: [rb_tree.insert(i) for i in range(test_size)],
            number=1
        )
        rb_search_time = timeit.timeit(
            lambda: [rb_tree.search(i) for i in range(0, test_size, 10)],
            number=100
        )

        # Dict operations
        test_dict = {}
        dict_insert_time = timeit.timeit(
            lambda: [test_dict.__setitem__(i, i) for i in range(test_size)],
            number=1
        )
        dict_search_time = timeit.timeit(
            lambda: [test_dict.get(i) for i in range(0, test_size, 10)],
            number=100
        )

        print(f"Insertion - RB Tree: {rb_insert_time:.6f}s, Dict: {dict_insert_time:.6f}s")
        print(f"Search - RB Tree: {rb_search_time:.6f}s, Dict: {dict_search_time:.6f}s")
        print(f"Insertion ratio (RB/Dict): {rb_insert_time/dict_insert_time:.2f}")
        print(f"Search ratio (RB/Dict): {rb_search_time/dict_search_time:.2f}")

    # Run all benchmarks
    benchmark_insertion()
    benchmark_search()
    benchmark_deletion()
    benchmark_memory_usage()
    compare_with_builtin_dict()

