"""
B-Tree Implementation

This module provides a complete B-Tree implementation with all standard operations
including search, insert, delete, and range queries. B-Trees are designed for
efficient external storage with guaranteed O(log n) performance.
"""

from typing import TypeVar, Generic, Optional, List, Iterator, Callable
import sys
from .btree_node import BTreeNode

T = TypeVar('T')

class BTree(Generic[T]):
    """
    A B-Tree implementation for efficient external storage.

    This B-Tree maintains the following properties:
    - Every node has at most 2t children
    - Every non-leaf node (except root) has at least t children
    - The root has at least 2 children if it's not a leaf
    - All leaves are at the same level
    - A non-leaf node with k children contains k-1 keys

    Args:
        min_degree: Minimum degree of the B-Tree (t â‰¥ 2)
        key_comparator: Optional custom comparator for keys
    """

    def __init__(self, min_degree: int = 3, key_comparator: Optional[Callable[[T, T], int]] = None) -> None:
        if min_degree < 2:
            raise ValueError("Minimum degree must be at least 2")

        self.min_degree = min_degree
        self.max_keys = 2 * min_degree - 1
        self.min_keys = min_degree - 1
        self.root: Optional[BTreeNode[T]] = None
        self.size = 0
        self.height = 0

        # Use custom comparator or default to < operator
        if key_comparator:
            self._compare = key_comparator
        else:
            self._compare = lambda x, y: -1 if x < y else (1 if x > y else 0)

    def __len__(self) -> int:
        """Return the number of keys in the B-Tree."""
        return self.size

    def __contains__(self, key: T) -> bool:
        """Check if a key exists in the B-Tree."""
        return self.search(key) is not None

    def is_empty(self) -> bool:
        """Check if the B-Tree is empty."""
        return self.root is None

    def clear(self) -> None:
        """Remove all keys from the B-Tree."""
        self.root = None
        self.size = 0
        self.height = 0

    def _create_node(self, is_leaf: bool) -> BTreeNode[T]:
        """Create a new B-Tree node."""
        return BTreeNode(
            keys=[None] * self.max_keys,
            children=[None] * (self.max_keys + 1) if not is_leaf else None,
            is_leaf=is_leaf,
            num_keys=0
        )

    def search(self, key: T) -> Optional[T]:
        """
        Search for a key in the B-Tree.

        Args:
            key: The key to search for

        Returns:
            The key if found, None otherwise
        """
        if self.root is None:
            return None

        return self._search_recursive(self.root, key)

    def _search_recursive(self, node: BTreeNode[T], key: T) -> Optional[T]:
        """Recursively search for a key in a subtree."""
        i = 0

        # Find the first key greater than or equal to the search key
        while i < node.num_keys and self._compare(key, node.keys[i]) > 0:
            i += 1

        # If we found the key, return it
        if i < node.num_keys and self._compare(key, node.keys[i]) == 0:
            return node.keys[i]

        # If this is a leaf, the key is not in the tree
        if node.is_leaf:
            return None

        # Otherwise, search in the appropriate child
        return self._search_recursive(node.children[i], key)

    def insert(self, key: T) -> None:
        """
        Insert a key into the B-Tree.

        Args:
            key: The key to insert
        """
        if self.root is None:
            # Create the first node
            self.root = self._create_node(is_leaf=True)
            self.root.keys[0] = key
            self.root.num_keys = 1
            self.size = 1
            self.height = 1
        else:
            # If the root is full, split it
            if self.root.num_keys == self.max_keys:
                old_root = self.root
                self.root = self._create_node(is_leaf=False)
                self.root.children[0] = old_root
                self._split_child(self.root, 0, old_root)
                self.height += 1

            # Insert the key
            self._insert_non_full(self.root, key)
            self.size += 1

    def _insert_non_full(self, node: BTreeNode[T], key: T) -> None:
        """Insert a key into a non-full node."""
        i = node.num_keys - 1

        if node.is_leaf:
            # Find the position to insert the key
            while i >= 0 and self._compare(key, node.keys[i]) < 0:
                node.keys[i + 1] = node.keys[i]
                i -= 1

            # Insert the key
            node.keys[i + 1] = key
            node.num_keys += 1
        else:
            # Find the child to insert into
            while i >= 0 and self._compare(key, node.keys[i]) < 0:
                i -= 1
            i += 1

            # If the child is full, split it
            if node.children[i].num_keys == self.max_keys:
                self._split_child(node, i, node.children[i])

                # Determine which child to insert into
                if self._compare(key, node.keys[i]) > 0:
                    i += 1

            # Insert into the child
            self._insert_non_full(node.children[i], key)

    def _split_child(self, parent: BTreeNode[T], child_index: int, child: BTreeNode[T]) -> None:
        """Split a full child node."""
        # Create a new node for the right half
        new_child = self._create_node(is_leaf=child.is_leaf)
        new_child.num_keys = self.min_keys

        # Copy the right half of keys
        for j in range(self.min_keys):
            new_child.keys[j] = child.keys[j + self.min_keys + 1]

        # Copy the right half of children (if not a leaf)
        if not child.is_leaf:
            for j in range(self.min_keys + 1):
                new_child.children[j] = child.children[j + self.min_keys + 1]

        # Update the original child
        child.num_keys = self.min_keys

        # Make room for the new child in the parent
        for j in range(parent.num_keys, child_index, -1):
            parent.children[j + 1] = parent.children[j]

        # Insert the new child
        parent.children[child_index + 1] = new_child

        # Make room for the promoted key
        for j in range(parent.num_keys - 1, child_index - 1, -1):
            parent.keys[j + 1] = parent.keys[j]

        # Promote the middle key
        parent.keys[child_index] = child.keys[self.min_keys]
        parent.num_keys += 1

    def delete(self, key: T) -> bool:
        """
        Delete a key from the B-Tree.

        Args:
            key: The key to delete

        Returns:
            True if the key was deleted, False if it wasn't found
        """
        if self.root is None:
            return False

        # Delete the key
        deleted = self._delete_recursive(self.root, key)

        if deleted:
            self.size -= 1

            # If the root becomes empty, update the tree
            if self.root.num_keys == 0:
                if self.root.is_leaf:
                    self.root = None
                    self.height = 0
                else:
                    self.root = self.root.children[0]
                    self.height -= 1

        return deleted

    def _delete_recursive(self, node: BTreeNode[T], key: T) -> bool:
        """Recursively delete a key from a subtree."""
        i = 0

        # Find the key or the child to search in
        while i < node.num_keys and self._compare(key, node.keys[i]) > 0:
            i += 1

        if node.is_leaf:
            # Key is in this leaf node
            if i < node.num_keys and self._compare(key, node.keys[i]) == 0:
                # Remove the key
                for j in range(i, node.num_keys - 1):
                    node.keys[j] = node.keys[j + 1]
                node.num_keys -= 1
                return True
            return False
        else:
            # Key is in a child node
            if i < node.num_keys and self._compare(key, node.keys[i]) == 0:
                # Key is in this node, replace it with predecessor or successor
                return self._delete_internal_node(node, key, i)
            else:
                # Key is in a child
                return self._delete_from_child(node, key, i)

    def _delete_internal_node(self, node: BTreeNode[T], key: T, key_index: int) -> bool:
        """Delete a key from an internal node."""
        child = node.children[key_index]
        right_child = node.children[key_index + 1]

        if child.num_keys > self.min_keys:
            # Replace with predecessor
            predecessor = self._get_predecessor(child)
            node.keys[key_index] = predecessor
            return self._delete_recursive(child, predecessor)
        elif right_child.num_keys > self.min_keys:
            # Replace with successor
            successor = self._get_successor(right_child)
            node.keys[key_index] = successor
            return self._delete_recursive(right_child, successor)
        else:
            # Both children have minimum keys, merge them
            self._merge_children(node, key_index)
            return self._delete_recursive(child, key)

    def _delete_from_child(self, node: BTreeNode[T], key: T, child_index: int) -> bool:
        """Delete a key from a child node."""
        child = node.children[child_index]

        if child.num_keys == self.min_keys:
            # Ensure the child has enough keys
            self._ensure_child_has_keys(node, child_index)

            # Update child index if it changed
            if child_index > 0 and child_index < node.num_keys:
                if self._compare(key, node.keys[child_index - 1]) <= 0:
                    child = node.children[child_index - 1]
                elif self._compare(key, node.keys[child_index]) > 0:
                    child = node.children[child_index + 1]
            elif child_index == 0:
                if self._compare(key, node.keys[0]) > 0:
                    child = node.children[1]
            else:
                if self._compare(key, node.keys[child_index - 1]) <= 0:
                    child = node.children[child_index - 1]

        return self._delete_recursive(child, key)

    def _get_predecessor(self, node: BTreeNode[T]) -> T:
        """Get the predecessor of a key (rightmost key in left subtree)."""
        while not node.is_leaf:
            node = node.children[node.num_keys]
        return node.keys[node.num_keys - 1]

    def _get_successor(self, node: BTreeNode[T]) -> T:
        """Get the successor of a key (leftmost key in right subtree)."""
        while not node.is_leaf:
            node = node.children[0]
        return node.keys[0]

    def _ensure_child_has_keys(self, parent: BTreeNode[T], child_index: int) -> None:
        """Ensure a child has enough keys by borrowing from siblings or merging."""
        child = parent.children[child_index]
        left_sibling = parent.children[child_index - 1] if child_index > 0 else None
        right_sibling = parent.children[child_index + 1] if child_index < parent.num_keys else None

        # Try to borrow from left sibling
        if left_sibling and left_sibling.num_keys > self.min_keys:
            self._borrow_from_left_sibling(parent, child_index, left_sibling, child)
        # Try to borrow from right sibling
        elif right_sibling and right_sibling.num_keys > self.min_keys:
            self._borrow_from_right_sibling(parent, child_index, right_sibling, child)
        # Merge with left sibling
        elif left_sibling:
            self._merge_children(parent, child_index - 1)
        # Merge with right sibling
        elif right_sibling:
            self._merge_children(parent, child_index)

    def _borrow_from_left_sibling(self, parent: BTreeNode[T], child_index: int, 
                                 left_sibling: BTreeNode[T], child: BTreeNode[T]) -> None:
        """Borrow a key from the left sibling."""
        # Make room for the borrowed key
        for i in range(child.num_keys, 0, -1):
            child.keys[i] = child.keys[i - 1]
        if not child.is_leaf:
            for i in range(child.num_keys + 1, 0, -1):
                child.children[i] = child.children[i - 1]

        # Borrow the key from parent
        child.keys[0] = parent.keys[child_index - 1]
        child.num_keys += 1

        # Move the rightmost key from left sibling to parent
        parent.keys[child_index - 1] = left_sibling.keys[left_sibling.num_keys - 1]

        # Move the rightmost child from left sibling
        if not left_sibling.is_leaf:
            child.children[0] = left_sibling.children[left_sibling.num_keys]

        left_sibling.num_keys -= 1

    def _borrow_from_right_sibling(self, parent: BTreeNode[T], child_index: int,
                                  right_sibling: BTreeNode[T], child: BTreeNode[T]) -> None:
        """Borrow a key from the right sibling."""
        # Borrow the key from parent
        child.keys[child.num_keys] = parent.keys[child_index]
        child.num_keys += 1

        # Move the leftmost key from right sibling to parent
        parent.keys[child_index] = right_sibling.keys[0]

        # Move the leftmost child from right sibling
        if not right_sibling.is_leaf:
            child.children[child.num_keys] = right_sibling.children[0]

        # Remove the borrowed key from right sibling
        for i in range(right_sibling.num_keys - 1):
            right_sibling.keys[i] = right_sibling.keys[i + 1]
        if not right_sibling.is_leaf:
            for i in range(right_sibling.num_keys):
                right_sibling.children[i] = right_sibling.children[i + 1]

        right_sibling.num_keys -= 1

    def _merge_children(self, parent: BTreeNode[T], key_index: int) -> None:
        """Merge two children of a parent node."""
        left_child = parent.children[key_index]
        right_child = parent.children[key_index + 1]

        # Move the key from parent to left child
        left_child.keys[left_child.num_keys] = parent.keys[key_index]
        left_child.num_keys += 1

        # Move all keys and children from right child to left child
        for i in range(right_child.num_keys):
            left_child.keys[left_child.num_keys] = right_child.keys[i]
            left_child.children[left_child.num_keys + 1] = right_child.children[i]
            left_child.num_keys += 1

        # Move the keys from parent to left child
        for i in range(key_index, parent.num_keys - 1):
            parent.keys[i] = parent.keys[i + 1]

        # Move the children from parent to left child
        for i in range(key_index + 1, parent.num_keys):
            parent.children[i] = parent.children[i + 1]

        parent.num_keys -= 1

        # Remove the right child
        parent.children[key_index + 1] = None

    def range_query(self, start_key: T, end_key: T) -> List[T]:
        """
        Find all keys in the range [start_key, end_key].

        Args:
            start_key: Start of the range (inclusive)
            end_key: End of the range (inclusive)

        Returns:
            List of keys in the range
        """
        result = []
        if self.root is not None:
            self._range_query_recursive(self.root, start_key, end_key, result)
        return result

    def _range_query_recursive(self, node: BTreeNode[T], start_key: T, end_key: T, result: List[T]) -> bool:
        """
        Recursively find keys in the range [start_key, end_key].
        Returns True if should continue searching (haven't exceeded end_key).
        """
        i = 0

        # Find the first key >= start_key
        while i < node.num_keys and self._compare(start_key, node.keys[i]) > 0:
            i += 1

        if node.is_leaf:
            while i < node.num_keys and self._compare(node.keys[i], end_key) <= 0:
                result.append(node.keys[i])
                i += 1
            return i < node.num_keys  # Continue if more keys to check
        else:
            # Search in children with early termination
            while i < node.num_keys:
                if not self._range_query_recursive(node.children[i], start_key, end_key, result):
                    return False

                if self._compare(node.keys[i], end_key) > 0:
                    return False  # Stop searching

                if self._compare(node.keys[i], start_key) >= 0:
                    result.append(node.keys[i])

                i += 1

            return self._range_query_recursive(node.children[i], start_key, end_key, result)

    def inorder_traversal(self) -> Iterator[T]:
        """Perform an inorder traversal of the B-Tree."""
        if self.root is not None:
            yield from self._inorder_recursive(self.root)

    def _inorder_recursive(self, node: BTreeNode[T]) -> Iterator[T]:
        """Recursively perform inorder traversal."""
        if node.is_leaf:
            for i in range(node.num_keys):
                yield node.keys[i]
        else:
            for i in range(node.num_keys):
                yield from self._inorder_recursive(node.children[i])
                yield node.keys[i]
            yield from self._inorder_recursive(node.children[node.num_keys])

    def get_height(self) -> int:
        """Get the height of the B-Tree."""
        return self.height

    def get_memory_usage(self) -> int:
        """Calculate total memory usage of the B-Tree."""
        if self.root is None:
            return sys.getsizeof(self)

        total_size = sys.getsizeof(self)
        total_size += self._get_node_memory_usage(self.root)
        return total_size

    def _get_node_memory_usage(self, node: BTreeNode[T]) -> int:
        """Calculate memory usage of a node and its children."""
        total_size = node.get_memory_size()

        if not node.is_leaf and node.children:
            for i in range(node.num_keys + 1):
                if node.children[i]:
                    total_size += self._get_node_memory_usage(node.children[i])

        return total_size

    def __repr__(self) -> str:
        if self.root is None:
            return "BTree()"

        keys = list(self.inorder_traversal())
        return f"BTree({keys})"

    def __iter__(self) -> Iterator[T]:
        return self.inorder_traversal()

"""
Spell Checker Implementation

This module provides a spell checker using trie for dictionary lookup
with basic suggestion capabilities.
"""

from typing import List, Optional, Tuple
from src.chapter_10.trie import Trie

class SpellChecker:
    """
    A spell checker using trie for dictionary lookup.

    This spell checker demonstrates how to use tries for
    efficient dictionary-based spell checking.
    """

    def __init__(self, dictionary: Optional[List[str]] = None) -> None:
        """
        Initialize the spell checker.

        Args:
            dictionary: List of correctly spelled words
        """
        self._trie = Trie[bool]()
        if dictionary:
            self.add_dictionary(dictionary)

    def add_dictionary(self, words: List[str]) -> None:
        """Add words to the dictionary."""
        for word in words:
            if word:  # Skip empty strings
                self._trie.insert(word.lower(), True)

    def add_word(self, word: str) -> None:
        """Add a single word to the dictionary."""
        if word:
            self._trie.insert(word.lower(), True)

    def is_correct(self, word: str) -> bool:
        """Check if a word is spelled correctly."""
        if not word:
            return False
        return self._trie.search(word.lower()) is not None

    def get_suggestions(self, misspelled_word: str, max_suggestions: int = 5) -> List[str]:
        """
        Get spelling suggestions for a misspelled word.

        This is a simplified implementation. In practice, you'd use
        edit distance algorithms like Levenshtein distance.
        """
        suggestions = []
        word = misspelled_word.lower()

        if not word:
            return suggestions

        # Check for common prefixes
        for i in range(1, len(word) + 1):
            prefix = word[:i]
            if self._trie.starts_with(prefix):
                # Get words with this prefix
                prefix_words = self._trie.get_all_with_prefix(prefix)
                for suggestion, _ in prefix_words:
                    if suggestion not in suggestions:
                        suggestions.append(suggestion)
                        if len(suggestions) >= max_suggestions:
                            break

        return suggestions[:max_suggestions]

    def check_text(self, text: str) -> List[Tuple[str, int, List[str]]]:
        """
        Check spelling in a text.

        Returns:
            List of (word, position, suggestions) tuples
        """
        import re

        # Split text into words, preserving punctuation
        words = re.findall(r'\b\w+\b', text)
        errors = []

        for i, word in enumerate(words):
            # Remove punctuation for checking
            clean_word = ''.join(c for c in word if c.isalpha())
            if clean_word and not self.is_correct(clean_word):
                suggestions = self.get_suggestions(clean_word)
                errors.append((word, i, suggestions))

        return errors

    def remove_word(self, word: str) -> bool:
        """Remove a word from the dictionary."""
        if word:
            return self._trie.delete(word.lower())
        return False

    def get_dictionary_size(self) -> int:
        """Get the number of words in the dictionary."""
        return len(self._trie)

    def get_all_words(self) -> List[str]:
        """Get all words in the dictionary."""
        return [word for word, _ in self._trie.get_all_strings()]

    def __contains__(self, word: str) -> bool:
        """Check if a word is in the dictionary."""
        return self.is_correct(word)

"""
Unicode Trie Implementation

This module provides a Unicode-aware trie implementation that handles
Unicode normalization, case folding, and proper character comparison.
"""

import unicodedata
from typing import TypeVar, Generic, Optional, List, Tuple
from src.chapter_10.trie import Trie, TrieNode

T = TypeVar('T')

class UnicodeTrie(Trie[T]):
    """
    A Unicode-aware trie implementation.

    This trie handles Unicode normalization, case folding, and
    proper character comparison for international text.
    """

    def __init__(self, normalize: bool = True, case_sensitive: bool = True) -> None:
        """
        Initialize a Unicode-aware trie.

        Args:
            normalize: Whether to normalize Unicode strings (NFC)
            case_sensitive: Whether to preserve case
        """
        super().__init__()
        self._normalize = normalize
        self._case_sensitive = case_sensitive

    def _normalize_string(self, s: str) -> str:
        """Normalize a string according to trie settings."""
        if self._normalize:
            s = unicodedata.normalize('NFC', s)
        if not self._case_sensitive:
            s = s.casefold()
        return s

    def insert(self, key: str, value: Optional[T] = None) -> None:
        """Insert a Unicode string into the trie."""
        normalized_key = self._normalize_string(key)
        super().insert(normalized_key, value)

    def search(self, key: str) -> Optional[T]:
        """Search for a Unicode string in the trie."""
        normalized_key = self._normalize_string(key)
        return super().search(normalized_key)

    def starts_with(self, prefix: str) -> bool:
        """Check if any string starts with the given Unicode prefix."""
        normalized_prefix = self._normalize_string(prefix)
        return super().starts_with(normalized_prefix)

    def get_all_with_prefix(self, prefix: str) -> List[Tuple[str, T]]:
        """Get all strings with the given Unicode prefix."""
        normalized_prefix = self._normalize_string(prefix)
        return super().get_all_with_prefix(normalized_prefix)

    def autocomplete(self, prefix: str, max_results: int = 10) -> List[str]:
        """Get autocomplete suggestions for a Unicode prefix."""
        normalized_prefix = self._normalize_string(prefix)
        return super().autocomplete(normalized_prefix, max_results)

    def delete(self, key: str) -> bool:
        """Delete a Unicode string from the trie."""
        normalized_key = self._normalize_string(key)
        return super().delete(normalized_key)

    def __contains__(self, key: str) -> bool:
        """Check if a Unicode string is stored in the trie."""
        normalized_key = self._normalize_string(key)
        return super().__contains__(normalized_key)

    def __getitem__(self, key: str) -> T:
        """Get the value associated with a Unicode key."""
        normalized_key = self._normalize_string(key)
        return super().__getitem__(normalized_key)

    def __setitem__(self, key: str, value: T) -> None:
        """Set the value associated with a Unicode key."""
        normalized_key = self._normalize_string(key)
        super().__setitem__(normalized_key, value)

    def __delitem__(self, key: str) -> None:
        """Remove a Unicode key from the trie."""
        normalized_key = self._normalize_string(key)
        super().__delitem__(normalized_key)

"""
Binary Heap Implementation

This module provides a complete binary heap implementation supporting both
min and max heaps with O(log n) operations and O(n) heapify construction.
"""

from typing import TypeVar, Generic, Optional, List, Callable, Any, Iterator
from dataclasses import dataclass

T = TypeVar('T')

@dataclass
class HeapNode(Generic[T]):
    """A node in the binary heap with priority and optional data."""
    priority: int
    data: Optional[T] = None

    def __lt__(self, other: 'HeapNode[T]') -> bool:
        # Only compare by priority for simplicity
        return self.priority < other.priority

    def __eq__(self, other: Any) -> bool:
        if not isinstance(other, HeapNode):
            return False
        return self.priority == other.priority and self.data == other.data

    def __repr__(self) -> str:
        if self.data is not None:
            return f"HeapNode({self.priority}, {self.data})"
        return f"HeapNode({self.priority})"

class BinaryHeap(Generic[T]):
    """
    A binary heap implementation supporting both min and max heaps.

    This implementation provides:
    - O(log n) insertion and extraction
    - O(n) heapify construction
    - O(1) peek operations
    - Support for custom comparison functions
    - Memory-efficient array-based storage
    """

    def __init__(self, heap_type: str = "min", key_func: Optional[Callable[[T], int]] = None) -> None:
        """
        Initialize a binary heap.

        Args:
            heap_type: "min" for min-heap, "max" for max-heap
            key_func: Function to extract priority from data items
        """
        self._heap: List[HeapNode[T]] = []
        self._heap_type = heap_type.lower()
        self._key_func = key_func

        if self._heap_type not in ("min", "max"):
            raise ValueError("Heap type must be 'min' or 'max'")

    def __len__(self) -> int:
        return len(self._heap)

    def is_empty(self) -> bool:
        return len(self._heap) == 0

    def push(self, item: T, priority: Optional[int] = None) -> None:
        """
        Add an item to the heap.

        Args:
            item: The item to add
            priority: Priority value (if None, uses key_func or item itself)
        """
        if priority is None:
            if self._key_func:
                priority = self._key_func(item)
            elif hasattr(item, 'priority'):
                # Handle PriorityQueueItem and similar objects
                priority = item.priority
            elif isinstance(item, (int, float)):
                priority = item
            else:
                raise ValueError("Must provide priority or key_func for non-numeric items")

        node = HeapNode(priority, item)
        self._heap.append(node)
        self._sift_up(len(self._heap) - 1)

    def pop(self) -> T:
        """
        Remove and return the highest priority item.

        Returns:
            The highest priority item

        Raises:
            IndexError: If heap is empty
        """
        if self.is_empty():
            raise IndexError("Heap is empty")

        if len(self._heap) == 1:
            return self._heap.pop().data

        # Swap root with last element
        root = self._heap[0]
        self._heap[0] = self._heap.pop()

        # Restore heap property
        self._sift_down(0)

        return root.data

    def peek(self) -> T:
        """
        Return the highest priority item without removing it.

        Returns:
            The highest priority item

        Raises:
            IndexError: If heap is empty
        """
        if self.is_empty():
            raise IndexError("Heap is empty")
        return self._heap[0].data

    def peek_priority(self) -> int:
        """
        Return the priority of the highest priority item.

        Returns:
            The priority value

        Raises:
            IndexError: If heap is empty
        """
        if self.is_empty():
            raise IndexError("Heap is empty")
        return self._heap[0].priority

    def _parent(self, index: int) -> int:
        """Get the parent index of a given index."""
        return (index - 1) // 2

    def _left_child(self, index: int) -> int:
        """Get the left child index of a given index."""
        return 2 * index + 1

    def _right_child(self, index: int) -> int:
        """Get the right child index of a given index."""
        return 2 * index + 2

    def _has_parent(self, index: int) -> bool:
        """Check if an index has a parent."""
        return index > 0

    def _has_left_child(self, index: int) -> bool:
        """Check if an index has a left child."""
        return self._left_child(index) < len(self._heap)

    def _has_right_child(self, index: int) -> bool:
        """Check if an index has a right child."""
        return self._right_child(index) < len(self._heap)

    def _swap(self, index1: int, index2: int) -> None:
        """Swap two elements in the heap."""
        self._heap[index1], self._heap[index2] = self._heap[index2], self._heap[index1]

    def _should_swap_up(self, child_index: int, parent_index: int) -> bool:
        """Determine if child should swap with parent based on heap type."""
        if self._heap_type == "min":
            return self._heap[child_index] < self._heap[parent_index]
        else:  # max heap
            return self._heap[child_index] > self._heap[parent_index]

    def _should_swap_down(self, parent_index: int, child_index: int) -> bool:
        """Determine if parent should swap with child based on heap type."""
        if self._heap_type == "min":
            return self._heap[parent_index] > self._heap[child_index]
        else:  # max heap
            return self._heap[parent_index] < self._heap[child_index]

    def _sift_up(self, index: int) -> None:
        """Move an element up the heap to restore heap property."""
        while self._has_parent(index) and self._should_swap_up(index, self._parent(index)):
            self._swap(index, self._parent(index))
            index = self._parent(index)

    def _sift_down(self, index: int) -> None:
        """Move an element down the heap to restore heap property."""
        while self._has_left_child(index):
            # Find the smaller/larger child
            child_index = self._left_child(index)
            if (self._has_right_child(index) and 
                self._should_swap_down(child_index, self._right_child(index))):
                child_index = self._right_child(index)

            # If parent is already in correct position, stop
            if not self._should_swap_down(index, child_index):
                break

            self._swap(index, child_index)
            index = child_index

    def heapify(self, items: List[T], priorities: Optional[List[int]] = None) -> None:
        """
        Build a heap from a list of items in O(n) time.

        Args:
            items: List of items to add to heap
            priorities: Optional list of priorities (must match items length)
        """
        self._heap.clear()

        if priorities is None:
            for item in items:
                self.push(item)
        else:
            if len(items) != len(priorities):
                raise ValueError("Items and priorities must have same length")
            for item, priority in zip(items, priorities):
                self.push(item, priority)

    def heapify_bottom_up(self, items: List[T], priorities: Optional[List[int]] = None) -> None:
        """
        Build a heap using bottom-up heapify in O(n) time.

        This is more efficient than repeated push operations for large datasets.
        """
        if priorities is None:
            if self._key_func:
                self._heap = [HeapNode(self._key_func(item), item) for item in items]
            elif all(hasattr(item, 'priority') for item in items):
                # Handle PriorityQueueItem and similar objects
                self._heap = [HeapNode(item.priority, item) for item in items]
            elif all(isinstance(item, (int, float)) for item in items):
                self._heap = [HeapNode(item, item) for item in items]
            else:
                raise ValueError("Must provide priorities or key_func for non-numeric items")
        else:
            if len(items) != len(priorities):
                raise ValueError("Items and priorities must have same length")
            self._heap = [HeapNode(priority, item) for item, priority in zip(items, priorities)]

        # Bottom-up heapify: start from last non-leaf node
        for i in range(self._parent(len(self._heap) - 1), -1, -1):
            self._sift_down(i)

    def __repr__(self) -> str:
        items = [f"{node.priority}:{node.data}" for node in self._heap]
        return f"BinaryHeap({self._heap_type}, [{', '.join(items)}])"

    def __iter__(self) -> 'Iterator[T]':
        """
        Iterate over heap items in priority order.

        Note: This method creates a complete copy of the heap in O(n) space.
        For large datasets, consider using pop() operations directly to avoid
        memory overhead.
        """
        # Create a copy to avoid modifying the original heap
        temp_heap = BinaryHeap[T](heap_type=self._heap_type, key_func=self._key_func)
        temp_heap._heap = [HeapNode(node.priority, node.data) for node in self._heap]

        while not temp_heap.is_empty():
            yield temp_heap.pop()

    def iter_destructive(self) -> 'Iterator[T]':
        """
        Iterate over heap items in priority order, consuming the heap.

        This method is more memory efficient than __iter__ but destroys
        the original heap. Use when you only need to iterate once.
        """
        while not self.is_empty():
            yield self.pop()

    def to_list(self) -> List[T]:
        """Convert heap to a list in priority order."""
        return list(self)

    def clear(self) -> None:
        """Clear all elements from the heap."""
        self._heap.clear()

    def size(self) -> int:
        """Get the number of elements in the heap."""
        return len(self._heap)

    def capacity(self) -> int:
        """Get the current capacity of the heap."""
        return len(self._heap)

    def is_valid(self) -> bool:
        """Check if the heap property is maintained."""
        for i in range(len(self._heap)):
            if self._has_left_child(i):
                if self._should_swap_down(i, self._left_child(i)):
                    return False
            if self._has_right_child(i):
                if self._should_swap_down(i, self._right_child(i)):
                    return False
        return True

    def merge(self, other: 'BinaryHeap[T]') -> 'BinaryHeap[T]':
        """
        Merge two heaps efficiently.

        This method creates a new heap containing all elements from both heaps.
        The resulting heap maintains the heap property.

        Args:
            other: Another binary heap to merge with

        Returns:
            New heap containing all elements from both heaps

        Raises:
            ValueError: If heaps have different types or key functions
        """
        if self._heap_type != other._heap_type:
            raise ValueError("Cannot merge heaps with different types")

        if self._key_func != other._key_func:
            raise ValueError("Cannot merge heaps with different key functions")

        # Create new heap with same configuration
        merged_heap = BinaryHeap[T](heap_type=self._heap_type, key_func=self._key_func)

        # Combine all elements
        all_nodes = self._heap + other._heap

        # Use bottom-up heapify for efficient construction
        merged_heap._heap = all_nodes
        for i in range(merged_heap._parent(len(merged_heap._heap) - 1), -1, -1):
            merged_heap._sift_down(i)

        return merged_heap

"""
Comprehensive benchmarking module for hash table implementations.

This module provides detailed performance analysis and comparison of all
hash table implementations against Python's built-in dict.
"""

import timeit
import random
import string
from typing import Type, Dict, List, Any, Callable
from collections import defaultdict
import statistics

from .hash_table import (
    HashTableInterface,
    SeparateChainingHashTable,
    LinearProbingHashTable,
    QuadraticProbingHashTable,
    DoubleHashingHashTable
)


def generate_random_strings(count: int, length: int = 10) -> List[str]:
    """Generate random strings for testing."""
    return [''.join(random.choices(string.ascii_letters, k=length)) 
            for _ in range(count)]


def generate_random_integers(count: int, max_val: int = 1000000) -> List[int]:
    """Generate random integers for testing."""
    return [random.randint(0, max_val) for _ in range(count)]


def benchmark_operation(
    hash_table_class: Type[HashTableInterface],
    operation: str,
    data_size: int,
    data_type: str = "strings"
) -> float:
    """Benchmark a specific operation on a hash table implementation."""

    if data_type == "strings":
        keys = generate_random_strings(data_size)
        values = generate_random_strings(data_size)
    else:
        keys = generate_random_integers(data_size)
        values = generate_random_integers(data_size)

    def setup():
        return hash_table_class(), keys, values

    def insert_operation():
        ht, k, v = setup()
        for key, value in zip(k, v):
            ht[key] = value
        return ht

    def lookup_operation():
        ht, k, v = setup()
        for key, value in zip(k, v):
            ht[key] = value

        # Lookup all keys
        for key in k:
            _ = ht[key]
        return ht

    def delete_operation():
        ht, k, v = setup()
        for key, value in zip(k, v):
            ht[key] = value

        # Delete half the keys
        for key in k[:len(k)//2]:
            del ht[key]
        return ht

    def mixed_operation():
        ht, k, v = setup()
        # Insert all
        for key, value in zip(k, v):
            ht[key] = value

        # Lookup some
        for key in k[:len(k)//3]:
            _ = ht[key]

        # Delete some
        for key in k[len(k)//3:2*len(k)//3]:
            del ht[key]

        # Insert more
        for key, value in zip(k[2*len(k)//3:], v[2*len(k)//3:]):
            ht[key] = value

        return ht

    operations = {
        'insert': insert_operation,
        'lookup': lookup_operation,
        'delete': delete_operation,
        'mixed': mixed_operation
    }

    if operation not in operations:
        raise ValueError(f"Unknown operation: {operation}")

    # Run benchmark
    timer = timeit.Timer(operations[operation])
    times = timer.repeat(repeat=5, number=1)

    # Return median time
    return statistics.median(times)


def benchmark_python_dict(
    operation: str,
    data_size: int,
    data_type: str = "strings"
) -> float:
    """Benchmark Python's built-in dict for comparison."""

    if data_type == "strings":
